css:
@-moz-document domain("web.furycloud.io") {
html {
    background-color: #1d1e20;
}

.container-fluid {
    background-color: #1d1e20;
    color: white;
}

.panel-body,
.well {
    background-color: #1F1B24 !important;
    color: white;
}

.panel-default > .panel-heading {
    background-color: #1F1B24 !important;
}

a {
    color: lightblue
}

.bs-wizard > .bs-wizard-step .bs-wizard-stepnum {
    color: lightgray
}

.popover-content {
    background-color: #1d1e20;
    border: 1px gray solid;
}

.list-group-item {
    background-color: #1d1e20;
}

.panel-title {
    color: gray;
}

.panel-default > .panel-heading,
.panel-heading {
    color: white;
}

.list-group-item {
    border: 1px solid #555;
}

.modal-content {
    background-color: #1d1e20;
    color: white;
}

.modal button.close {
    color: #fff;
    opacity: 1;
}

.hljs {
    background-color: #1d1e20;
    color: white;
}

.form-control {
    background-color: #555;
    color: white;
}

.form-control input::placeholder {
    color: lightgray !important;
    opacity: 1;
}

.form-control:disabled {
    color: #111;
    background-color: #999;
}


/* Fury new UI */
.odin-content {
    background-color: #1d1e20;
    color: white;
}

.andes-card,
.odin-menu {
    background-color: #555;
    color: white;
}

.odin-menu .subitems a {
    color: white;
}

.odin-menu label.odin-menu-content__item {
    color: #009ee3
}

.odin-menu label.odin-menu-content__item.active {
    color: #f73;
}

.odin-menu .subitems a:hover,
.odin-menu label.odin-menu-content__item:hover {
    color: #1d1e20;
    box-shadow: inset 0.35em 0 0 #1d1e20;
}

.card-binnacle .container .middle .middle .name-text {
    color: white;
}

.card-binnacle .body p.andes-badge__content,
.binnacle-with-filters .filters,
.andes-list__item {
    color: white !important;
    background-color: #1d1e20;
}
.andes-list__item-primary {
    color: white !important;
}
.andes-list__item-tertiary .technology-logo {
    filter: invert(1);
}

.binnacle-with-filters .filters .andes-checkbox__label {
    color: white !important;
}

.card-binnacle .time,
.card-binnacle .service-type {
    color: white !important;
}

.card-binnacle .container .side.left-not-important {
    background-color: #1d1e20;
}

.andes-table {
    color: white;
}

.andes-table .andes-table__header {
    background-color: #1d1e20;
}

.andes-table .andes-table__row:hover {
    background-color: #1d1e20;
}

.get-started-content {
    color: white;
}

.container-scope {
    background-color: #1d1e20;
}

.first-step svg,
.container-scope svg {
    filter: invert(1);
}
}

@-moz-document domain("docs.furycloud.io") {
html,
main {
    background-color: #1d1e20;
}
.main-content,
aside {
    background-color: #555;
}
.ch-box-lite,
.menu-sidebar {
    background-color: #1d1e20;
    color: white
}
.menu-sidebar .menu-link {
    color: #999
}
.menu-sidebar .menu-link:hover {
    background-color: #555;
    color: white
}
}
