css:
@-moz-document domain("g1.globo.com") {
.mc-body,
    .glb-grid,
    .content,
    .widget,
    .mc-bottom,
    body,
    html {
        background-color: #222 !important;
        color: azure !important;
        scrollbar-color: #666 #333;
    }
    
    .content-head__title,
    .content-head__subtitle,
    .content__signa-share,
    .content-publication-data__from,
    .content-publication-data__updated,
    .medium-22 > p,
    .content-media__description,
    .content-media .content-media__description,
    .content-blockquote,
    h2,
    .ellipsis-overflowing-child,
    .shadow-video-flow-video-info__category,
    .shadow-video-flow-video-info__issued,
    div.shadow-video-flow-navigation__button,
    ._s,
    .feed-post-metadata-section,
    .feed-post-header-chapeu,
    .feed-post-datetime,
    #bstn-rtcl > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > h2:nth-child(1),
    #bstn-rtcl > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > ul:nth-child(2) > li:nth-child(1) > div:nth-child(1) > div:nth-child(1),
    #bstn-rtcl div,
    .bstn-item-shape p,
    .bstn-item-shape td {
        color: azure !important;
    }

	.feed-post-body-title .gui-color-primary {
		color: white;
	}

	.feed-post-body-title .gui-color-primary:hover {
		color: #bfbfbf;
	}
    
	.bstn-hl-title, .gui-color-primary .gui-color-hover .gui-color-primary-bg-after {
		color: white;
	}

	.bstn-hl-title:hover, .gui-color-primary .gui-color-hover .gui-color-primary-bg-after {
		color: #bfbfbf;
	}

	.bstn-related .feed-post-body-title {
    	color: #858585;
	}

	.bstn-related:hover .feed-post-body-title {
    	color: #bfbfbf;
	}

	.gui-color-primary element.style {
		color: #858585;
	}

    ._b,
    .theme a {
		color: #858585;
        text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);
    }

	._b,
    .theme a:hover {
		color: #bfbfbf;
        text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);
    }      

	.content-entenda-o-caso__title {
		color: #999;
	}
    
    #barra-globocom,
    #glbbarrawidget {
        display: none;
    }
    
    img {
        filter: brightness(.8);
        /*filter: invert(70);*/
    }
    
    /* esconder os coment√°rios */
    .glbComentarios-lista,
    #boxComentarios {
        display: none;
    }
    
    .load-more {
        border: none;
    }
    
    .theme-bg-color-primary,
    .gui-color-primary-bg,
    .bstn-fd .bstn-item-shape::after {
        background-color: #444;
    }
    
    .header-navegacao-color {
        color: #444;
    }
    
    #menu-curtain,
    #menu-container,
    .menu-rounder--opened-all .menu-cascade .menu-level:first-child {
        background-color: #222 !important;
        color: azure !important;
        scrollbar-color: #666 #333;
    }
    
    .bstn-hl,
    .bstn-item-shape,
    #bstn-rtcl .bstn-item-shape {
        background-color: #333 !important;
        border: 1px solid #444;
        color: azure !important;
    }
    
    .bstn-hl-chapeu,
    .bstn-hl-summary {
        color: azure !important;
    }
}
