css:
@-moz-document domain("travis-ci.com"), domain("travis-ci.org") {
/*CSS Part for travis.com*/
.loading-page{
    background-color: black;
}

.top{
    background-color:#ffffff45;
}

.broadcasts{
    background-color:#454545;
}

.avatar__provider-icon{
    fill: black;
}

.navigation-nested{
    background-color: black;
    border: 1px solid #ffffff45;
}

.navigation-nested__header{
    color: #ffffff90;
    background-color: black;
}

.navigation-nested__header.username .username__text:after{
    background: linear-gradient(-90deg,#000 20%,rgba(255,255,255,0) 100%);
}

.navigation-nested .topbar-menu-item:hover{
    background-color:#ffffff45;
}

.navigation-nested .nav-nested{
    background-color:#ffffff45;
}

.topbar, .topbar a:not(.auth-button){
    color: #ffffff90;
}


.dashboard .centered, .dashboard .loading-page{
    background-color: black;
}

.rows{
    border: 1px solid #ffffff45;
}

.dropup-list{
    background-color: black;
}

.dropup-list li:last-of-type:after{
    background: black;
}

.dropup-list a, .dropup-list button{
    background-color: black;
}

.my-build .message-and-branch .row-content:after, .my-build .repo .row-content:after{
    background-image:linear-gradient(-90deg, rgb(0, 0, 0) 0px, rgba(255, 255, 255, 0) 100%);
}

::selection{
    color: black;
    background-color:#fff9;
}

#left, .main, .wrapper{
    background-color: black;
}

#left{
    border-right: 2px solid #ffffff45;
}

.search--sidebar .search-inner{
    border: 1px solid #ffffff45;
}

.search--sidebar .search-inner input{
    background: inherit;
}

.sidebar-list ul{
        border-bottom: .46rem solid black;
    background-color: #ffffff45;
}

.option-display{
    border: 1px solid #ffffff45;
}

.option-tofu{
    border-left: 1px solid #ffffff45;
}

.multi-signin-button__vcs-button, .option-dropdown{
    background-color: black;
}

body, html{
    background: black;
}

button{
    background: black;
}

.button{
    color: #ffffff30;
    background-color: #2a2a2a;
}


.settings-section{
    border-bottom: 3px solid #ffffff45;
}

.switch.active .switch-inner{
    background-color: #2e7f83;
}

.billing .billing-select-plan .switch.active .switch-inner, .switch .switch-inner{
    background-color:#5f5e5e;
}

.settings-cron, .settings-envvar, .settings-sshkey{
    background-color: #1b1b1b;
}

.env-var-name:after, .ssh-key-name:after{
    background: linear-gradient(-90deg,#1b1b1b 0,rgba(241,241,241,0) 100%)
}

.env-var-value{
    background-color:#393838
}

#env-var-secrecy p{
    color:#ffffff45;
}

#env-var-secrecy p code{
    background-color:#1b1a1a;
    border: 1px solid #1b1a1a;
}

#env-var-secrecy{
    box-shadow:0 0 15px 3px #ffffff45;
}

.travis-form .travis-form__field .travis-form__label{
    color:#ffffff45;
}

.travis-form .travis-form__field .travis-form__field-component, .ember-power-select-placeholder{
    color:#ffffff45;
}

.activate-repo-button, .ember-power-select-option[aria-selected=true]{
    background-color:#ffffff30;
}

.form-submit{
    color:#ffffff45;
    background-color:#282727;
}

input[type=email], input[type=number], input[type=password], input[type=text], select, textarea{
    border: 1px solid #ffffff45;
}

.travistab-nav--secondary a, .travistab-nav.travistab-nav--underline{
    border-bottom: 2px solid #ffffff45;
}

.travistab-nav--secondary a.active:after, .travistab-nav--secondary a:active:after, .travistab-nav--secondary a:hover:after{
    background-color: #ffffff45;
}

.repo-navigation{
    border-bottom: 2px #ffffff45 solid;
}

.job-matrix-name-separator{
    border-top: 1px #ffffff45 solid;
}

.action-button--cancel, .action-button--debug, .action-button--restart{
    border: 1px solid #ffffff45;
}

.sidebar-seperator{
    border-bottom: 2px solid #454545;
}

.tile{
    background-color: black;
    color: #767676;
}

.tile.booting, .tile.created, .tile.queued, .tile.received, .tile.started{
    background-image: linear-gradient(to right, #edde3f 1px, rgb(0, 0, 0) 40px);
}

.tile.passed{
    background-image: linear-gradient(to right, rgb(57,170,86) 1px, rgb(0, 0, 0) 40px);
}

.tile.errored, .tile.failed{
    background-image: linear-gradient(to right, rgb(219,69,69) 1px, rgb(0, 0, 0) 40px);
}

.build-header.booting, .build-header.created, .build-header.queued, .build-header.received, .build-header.started, .row-li.booting, .row-li.created, .row-li.queued, .row-li.received, .row-li.started{
    background-image: linear-gradient(to right,#edde3f 1px,#000 55px); 
    background-repeat:no-repeat;
    color: #ffffff45;
    border: 1px solid #edde3f;
}

.build-header.passed, .row-li.passed{
    background-image:  linear-gradient(to right, #39aa56 1px , rgb(0, 0, 0) 60px);
    background-repeat: no-repeat;
    color: #ffffff45;
    border: 1px solid #39aa56;
}

.build-header.errored, .build-header.failed, .row-li.errored, .row-li.failed{
    background-image:  linear-gradient(to right, rgb(219, 69, 69)  1px , rgb(0, 0, 0) 60px);
    background-repeat: no-repeat;
    color: #ffffff45;
    border: 1px solid #db4545 ;
}
  
.build-header.canceled, .row-li.canceled{
    background-image:  linear-gradient(to right, #9d9d9d  1px , rgb(0, 0, 0) 60px);
    background-repeat: no-repeat;
    color: #ffffff45;
    border: 1px solid #9d9d9d ;
}

.row-li.canceled{
     background-image:  linear-gradient(to right, #9d9d9d  1px , rgb(0, 0, 0) 60px);
    border: 1px solid #9d9d9d;
}

.branch-row .row-last-build:before{
    background-color: #454545;
}

.job-waiting-stages-container{
    border:2px solid #454545;
}

.job-waiting-stages-container .not-loaded-ring{
    border: 2px solid #454545;
}

.job-waiting-stages-container .loading-line-container{
    background-color: #454545;
}

.pr-row .row-branch:hover a{
    background-color: #454545;
}


.yml-messages{
    border: 1px solid #ffffff45;
}

.yml-messages .yml-message-line code{
    background-color: #141414;
}

.inner-yaml-container{
    border: 1px solid #ffffff45;
}

.inner-yaml-container button{
    border: 1px solid #ffffff45;
}

pre.codedisplay[class*=language-]{
    background-color: #000!important;
}

code[class*=language-], pre[class*=language-]{
    text-shadow: none;
}

pre.codedisplay[class*=language-] code{
    color: #ffffff94;
}

code[class*=language-] ::selection, code[class*=language-]::selection, pre[class*=language-] ::selection, pre[class*=language-]::selection{
    background: #767474d9;
}

.build-commit:after{
    background-image: linear-gradient(-90deg, rgb(0, 0, 0) 20%, rgba(255, 255, 255, 0) 100%);
}

.tile h2:after{
    background-image: linear-gradient(-90deg,rgba(0,0,0) 0px,rgba(255,255,255,0) 100%);
}

.fade-out:after{
    background-image: linear-gradient(-90deg, rgb(0, 0, 0) 30%, rgba(255, 255, 255, 0) 100%);
}

.pr-row .row-message:after, .pr-row .row-branch:after{
    background-image:linear-gradient(-90deg, rgb(0, 0, 0) 0px, rgba(255, 255, 255, 0) 100%);
}

.pr-row .two-line:first-of-type{
    border-right: 1px solid #ffffff45;
}

.build-tiles>.booting, .build-tiles>.created, .build-tiles>.queued, .build-tiles>.received, .build-tiles>.started{
    background-color:#edde3f5e;
}

.build-tiles>.passed{
    background-color: #39aa565e;
}

.build-tiles>.errored, .build-tiles>.failed{
    background-color: #db45455e;
}

.build-tiles>.canceled{
    background-color: #ffffff45;
}

.build-tiles li{
    border-right: 1px solid #ffffff45;
}

.request-item{
    border: 1px solid #ffffff45;
}

.request-item .row-item:first-of-type{
    border-right: 1px solid #ffffff45;
}

.request-item.approved:active, .request-item.approved:hover{
    background-color:#224322;
}

.request-item.approved:hover .fade-out:after{
    background:linear-gradient(-90deg,#224322 40%,#22432236 100%);
}

.request-item.rejected:active, .request-item.rejected:hover{
    background-color:#432222;
}

.request-item.rejected:hover .fade-out:after{
    background:linear-gradient(-90deg,#432222 40%,#43222236 100%);
}

.travis-modal-overlay{
    background-color:rgba(97, 96, 96, .26);
}

.bg-white{
    background-color:black;
}

.badge{
    color: #ff9100;
    background: #ff910045;
    border: 1px solid #ff9100;
}

.travis-form .travis-form__field:not(.travis-form__field--without-frame) .travis-form__field-component{
    box-shadow:inset 0 0 1px 1px #706e6e61;
}

.ember-power-select-trigger{
    background-color: black;
}

input[type=email], input[type=number], input[type=password], input[type=text], select, textarea{
    background: black;
}

.ember-power-select-dropdown.ember-basic-dropdown--transitioned-in, .ember-power-select-search-input{
    background-color: black;
    color: #ffffff45;
}

.ember-power-select-option--loading-message, .ember-power-select-option--no-matches-message, .ember-power-select-option--search-message{
    color: #ffffff45;
}

.ember-power-select-search-input, .ember-power-select-search-input:focus, .insights-controls .insights-privacy-selector .travis-form .travis-form__field--focused .travis-form__field-select {
    box-shadow:inset 0 0 1px 1px #ffffff45;
}

.ember-power-select-dropdown::after{
    box-shadow:none;
}

.travis-form .travis-form__field:not(.travis-form__field--without-frame) .travis-form__field-component{
    box-shadow: #ffffff45;
}

.travis-form .travis-form__field:not(.travis-form__field--without-frame) .travis-form__field-component:focus{
    box-shadow:none;
}

.ember-power-select-dropdown{
    transition:none;
}


.account .pseudo-avatar~.real-avatar, .build-tiles li, .profile .pseudo-avatar~.real-avatar{
    background-color: #ffffff45;
}

.footer{
    background:black;
    box-shadow:0 -2px 3px #ffffff1c
}

.footer address, .footer ul{
    color: #ffffff45;
}

/*CSS Part for changelog.travis.com*/

body{
    color: #ffffff45;
}

header{
    background-color: black;
    box-shadow: 0 1px 0 rgba(250, 250, 250, .3);
}

header h1 small a{
    color: #ffffff90;
}

header a{
    color: #ffffff60;
}

.changelogItem{
    border-bottom: 1px solid #ffffff45;
}

.changelogItem time a{
    color:rgb(115, 150, 186);
}

.changelogItem .content code, .changelogItem .content pre{
    background:#141414;
    color:#ffffff45;
}

.changelogItem .author{
    color: #ffffff90;
}

}
