css:
@-moz-document domain(timelessleaf.com) {
body { background: #181717;
    font-family: merriweather,serif;
    font-size: 16px;
    font-weight: 300;
    color: #FFFFFF;
    line-height: 1.7;
}
.home .entry-title {
    color: #FFF;
}
a, a:visited {
    text-decoration: none;
    color: #fff;
    transition: .1s;
}
.site-branding::before {
    background-image: url( https://i.imgur.com/SJNYGW7.jpg);
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    content: "";
    display: block;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -1;
}
figure {
    margin: 0;
}

.main-navigation ul ul {
    background: #020202;

}
.site-title, .site-title a {
    color: #793;
}
.site-description {
    color: #c2c2c2;

}
a:hover, a:active {
    color: yellow !important;
}
.entry-title {
    color: #FFFFFF;
}
.sidebar .widget-title {
    color: #FFFFFF;

}
.widget-border {
    background-color: #FFFFFF;

}
input[type="text"], input[type="email"], input[type="url"], input[type="tel"], input[type="password"], input[type="search"], textarea {
    font-family: merriweather,serif;
    padding: 15px;
    border: 3px solid #eee;
    background: #020202;
} 
    body, button, input, select, textarea {
    color: #FFFFFF !important;
    font-size: 16px;
    font-size: 1rem;
    line-height: 1.6;
    max-width: 100%;
    transition: .1s;
}
.entry-border {
    background-color: #FFFFFF;
}
.socialmenu {
    background-color: #020202;

}
.entry-cat {
    color: #969696;
}
.entry-content a, .entry-content a:visited {
    color: #FFF;
    border-bottom: 2px solid #8193a1;
}
#footer {
    background-color: #202020;
}
.site-info {
    background-color: #030303;
}
}
