css:
@-moz-document domain(platzi.com) {
    
    @media only screen and (min-width:600px) and (max-width: 1400px){
        .MaterialView{
            padding-left:32px;
            grid-template-columns:57% 43%;
            grid-template-rows:max-content;
            grid-template-areas:"video community";
            overflow:hidden;
            -webkit-box-sizing:border-box;
            -moz-box-sizing:border-box;
            box-sizing:border-box
        }
        .MaterialView-content,.MaterialView-content-wrapper{
            background-color:#0c1633
        }
        .MaterialView-type--video{
            padding-top:calc(56.25vw - 18px)
        }
        .MaterialView-type--video .MaterialView-video{
            max-height:calc(100vh - 52px - 56.25vw);
            overflow-y:scroll
        }
        .MaterialView-type--video .MaterialView-video-item{
            position:absolute;
            top:0;
            right:0;
            width: 100%;
            max-width: calc(100vw - 32px);
    /*         width:calc(100vw - 32px) */
        }
        .MaterialView-type--video .MaterialView-community-wrapper{
            max-height:calc(100vh - 52px - 56.25vw)
        }
        .MaterialView-type--lecture{
            grid-template-areas:"video community" "video community"
        }
        .MaterialView-type--lecture .MaterialView-video{
            max-height:calc(100vh - 52px)
        }
        .MaterialView-type--lecture .MaterialView-content{
            max-height:calc(100vh - 52px);
            overflow-y:scroll
        }
        .MaterialView--blocked{
            padding-top:25rem
        }
    }
}
