css:
@-moz-document domain("bo-aes.de") {
* {
    height: auto !important;
    width: auto !important;
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    bottom: 0 !important;
    border: none !important;
    font-family: sans-serif !important;
}

body {
    font-size: 1em !important;
}

#sidebar ul {
    width: 100% !important;
}

#sidebar,
#content {
    float: none !important;
}

#masthead,
marquee,
#sidebar div.auto-style2,
#content div a img.auto-style10,
#content p img {
    display: none !important;
}

#sidebar ul li,
#sidebar ul li a {
    display: inline;
}

#sidebar a {
    border: none;
}

#sidebar ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    overflow: hidden;
    background-color: #333;
}

#sidebar ul li {
    float: left;
}

#sidebar ul li a {
    display: block;
    color: white;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
}

/* Change the link color to #111 (black) on hover */
#sidebar ul li a:hover {
    background-color: #111;
}

body,
#footer {
    background-color: white;
}

#footer {
    border-top: 1px solid black;
}
}
