css:
@-moz-document url-prefix("http://127.0.0.1:61233/browser") {
/*
  GENERAL SITE
*/
.negative-space {
    background-color: #282828;
    color: #00897b;
    border: 1px solid #00564d;
}

.pg-panel-content {
    background-color: #282828;
    color: #00897b;
    border: 1px solid #00564d;
}

.aciTreeText {
    color: #00897b !important;
}
.aciTreePush {
    color: #00897b !important;
}

.aciTreeSelected {
    background-color: #4a4848;
}

.aciTree {
    background-color: #282828;
}

.aciTreeLine:hover {
    color: #00564d !important;
    background-color: #969696 !important;
}


.wcTabScroller {
    background-color: #282828;
}

.wcFrameButtonBar {
    background-color: #282828;
}

.wcPanelTab {
    color: #00564d;
}
.wcPanelTab:hover {
    background-color: #363636 !important;
}

.wcPanelTabActive {
    color: #00897b;
}

.wcFrameButton {
    color: #00564d;
}

.wcFrameButton:hover {
    background-color: #363636;
}
}

@-moz-document url-prefix("http://127.0.0.1:61233/datagrid") {
/*
=========================
    SQL IDE
=========================
*/
/*
    TOP TOOL BAR
*/
.editor-toolbar {
    background-color: #282828;
    border: 1px solid #00564d;
}

.btn-secondary {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b;
}

#btn-rows-limit {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b;
}


/*
    SQL EDITOR EXTERNAL
*/
.wcTabScroller {
    background-color: #282828 !important;
    border: 1px solid #00897b;
}
.wcFrameButtonBar {
    background-color: #282828 !important;
    border: 1px solid #00897b;
}

.wcPanelTab {
    color: #00564d;
}
.wcPanelTab:hover {
    background-color: #363636 !important;
}

.wcPanelTabActive {
    color: #00897b;
}

.CodeMirror-gutters {
    background-color: #363636 !important;
}
.CodeMirror-linenumber {
    color: #969696 !important;
}


/*
    SQL EDITOR INTERNAL
*/
.CodeMirror-scroll {
    background-color: #282828;
    color: #00897b;
    border: 1px solid #00564d;
}
.CodeMirror-activeline-background {
    background-color: #400000 !important;
    border: 1px dashed #00897b !important;
}
.CodeMirror-cursors {
    visibility: visible;
}
.CodeMirror-cursor {
    border: 1px solid #969696 !important;
}
.cm-operator {
    color: red !important;
}
.cm-punctuation {
    color: green !important;
}
.cm-builtin {
    color: grey !important;
}


/*
    LOWER PANE
*/
table {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}

.sql-editor-grid-container {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.sql-editor-explain {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.pg-panel-content {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.grid-canvas {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.slick-header-columns {
    background-color: #282828 !important;
    color: #00897b !important;
}
.slick-header-column {
    background-color: #282828 !important;
    color: #00897b !important;
}
.slick-row {
    background-color: #282828 !important;
    color: #00897b !important;
}
.slick-cell {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.pg-text-editor {
    background-color: #282828 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.pg-textarea {
    background-color: #363636 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
.sql-editor-message {
    background-color: #400000 !important;
    color: #00897b !important;
    border: 1px solid #00897b !important;
}
}
