css:
@-moz-document url-prefix("https://www.roblox.com/My/money.aspx"), url-prefix("https://www.roblox.com/My/Money.aspx"), url-prefix("https://www.roblox.com/my/money.aspx") {
html { height: 100% }
body { min-height: 100% }

body, .text {
    font-family: HCo Gotham SSm,Helvetica Neue,Helvetica,Arial,Lucida Grande,sans-serif !important
}

#Body, #rbx-body, .container-footer, #BodyWrapper{
    background-color: #232527
}

#TabsContentContainer, .RobuxColumn div, #MyTransactions_tab .loading, #TradeItems_tab .loading, #Summary_tab .loading{
    background-color: #393b3d
}

.MyMoneyPage #TabsContentContainer, .MyMoneyPage .WhiteSquareTabsContainer {
    width: 800px;
}

.MyMoneyPage #TabsContentContainer {
    padding: 20px;
    border-radius: 5pt
}

#AdContainer, #AdvertisingLeaderboard {
    display: none
}

.MyMoneyPage.text {
    margin-left: auto;
    margin-right: auto;
    padding-top: 10px;
    width: 840px;
}

.text, span.form-label, .text .light {
    color: #edecec;
    font-size: 11pt
}


/* that drop down on the botton */
.input-group-btn .input-dropdown-btn {
    background-color: #bb121200;
    border-color: #bdbebe;
    color: hsla(0,0%,100%,.7);
}

.input-group-btn .input-dropdown-btn:hover {
    color: #fff;
    background-color: #ea8100;
    border: 1px solid #ea8100
}

.input-group-btn .input-dropdown-btn:focus {
    color: #fff;
    background-color: #ea8100;
    border: 1px solid #ea8100
}

/* tables */

.table {
    padding-top: 10px
}

.table td {
    color: #dedede;
    border-top: 1px solid #585a5e;
    padding: 7px

}

.table th {
    border: 0px;
    padding: 10px
}


.table-header .first {
    border-left: #919191
}

.table-header th {
    background-color: #282a2d;
}

.text-link.ViewTradeLink, td.Description a, #trade-help-link {
    color: #6eabef
}

.RobuxColumn.full-column div {
    width: 776px
}

#TabsContentContainer {
    margin-top: 2px
}


/* icons */
td span.icon-robux-16x16 {
    background-position: 0px -40px
}

.icon-down-16x16 {
    background-position: -17px -272px
}


.status-confirm {
    color: #343c48;
    background-color: #8caee1;
    border: 1px solid #3a5c7d;
    margin-bottom: 7pt
}


.BuyRobuxButton, .TipsyImg.tooltip-right {
    display: none
}

/* top tabs */
.WhiteSquareTabsContainer {
    background: none;
    margin-bottom: 10px;
    padding: 0 0 0 0;
    width: 840px
}

.SquareTabsContainer {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 840px;
    height: 100%
}


.SquareTabGray {
    background-color: #393b3d;
    border: none;
    flex: 1;
    height: 100%;
    border-radius: 5px;
    text-align: center;
}

.SquareTabGray span a {
    color: #e3e1e1;
    font-size: 15pt;
    font-weight: bold
}

.SquareTabGray:hover {
    background-color: #4b4d51;
}

.SquareTabGray:hover span a {
    background-color: inherit;

}

.SquareTabGray.selected {
    background-color: #bcbdbf;
}

.SquareTabGray.selected span a {
    color: inherit
}

.SquareTabGray.selected:hover {
    background-color: #cccdce;
}

.SquareTabGray.selected:hover span a {
    background-color: inherit
}

#TradeRequest, .unifiedModalContent, .ConfirmationModal, .GenericModalBody{
    border-radius: 10px;
    background-color: #343434
}
.unifiedModal, .ConfirmationModal .Title, #TradeRequest .TradeRequestText, .OfferHeader{
    color: White;
    font-weight: lighter;
}
.Message, .ConfirmationModalFooter{
    font-weight: 100 !important;
    color: White;
}
.BlankItem.LargeInventoryItem, .RobuxTradeRequestItem, .LargeInventoryItem .InventoryItemContainerInner, .LargeInventoryItem{
    background: #1e1e1e;
    border: 0px;
    border-radius: 5px;
}
.LargeInventoryItem{
    background: #0000;
    border: 0px
}
#TradeRequest .LargeInventoryItem:hover .InventoryItemContainerInner{
    border: 0px;
}
.icon-robux-16x16, .InventoryItemContainerInner .icon-robux-16x16
{
    background-image: url(https://images.rbxcdn.com/4521c2f80ff092cfe1d678e68b1deaca-economy_small_light.svg);
    background-position: 0px -40px
}
#TradeRequest .OfferContainer > img {
    content: url(https://i.imgur.com/0rvuBHi.png)
}
.ItemInfoLabel, .SerialNumberTotal, .InventoryItemSerial, .InventoryItemAveragePrice, .InventoryItemOriginalPrice{
    font-family: HCo Gotham SSm,Helvetica Neue,Helvetica,Arial,Lucida Grande,sans-serif;
    font-weight: 100;
    font-size: 7px !important;
    color: #c4c4c4 !important
}
.TradeExpiration{
   color: #c4c4c4 !important 
}
.OfferHeader{
    font-size: 13px
}
.InventoryItemContainerInner .InventoryItemSerial, .InventoryItemContainerInner .SerialNumberTotal, .InventoryItemContainerInner:hover .ItemInfoLabel{
    color: White
}
.btn-neutral, .btn-neutral:link, .btn-neutral:active, .btn-neutral:visited, .btn-negative, .btn-negative:link, .btn-negative:active, .btn-negative:visited{
    background-image: None;
    border: 0px;
    border-radius: 10px;
    background-color: #8a8a8a;
    padding: 11px 13px 0 13px;
}
.btn-neutral:hover, .btn-negative:hover{
    background-image: None;
    border: 0px;
    border-radius: 10px;
    background-color: #b7b7b7;
    padding: 11px 13px 0 13px;
}
#TradeRequest .LeftContentContainer {
    border-right: 1px solid #888;
}
.InventoryItemName, .TradeRequestText > a{
    color: #6eabef
}
/*leave this stuff below even if you dont use rplus it wont hurt, also the more records button code is down here so yeah dont remove*/
.btn-small{
    font-size: 10px !important;
    padding: 1px 13px 0 13px !important;
}
.btn-disabled-neutral, .btn-disabled-neutral:link, .btn-disabled-neutral:active, .btn-disabled-neutral:visited, .btn-control.btn-control-small{
    background-color: #b7b7b7;
    background-image: None;
    border-color: #b7b7b7;
    color: white;
    border-radius: 10px;
}
/*dropdown menu*/
.open .dropdown-menu{
    background-color: #101010 !important
}
}
