css:
@-moz-document url(https://roamresearch.com/) {

/*Left Side Bar */
.roam-sidebar-container {
    background-color: #000 !important;
    border-right: 1px solid #5C7080;
    box-sizing: border-box;
}

.roam-body .roam-app .roam-sidebar-container .roam-sidebar-content .log-button {
    color: #D9D9D9 !important;
}

.roam-body .roam-app .roam-sidebar-container .roam-sidebar-content .log-button:hover {
    background-color: #14171C !important;
    border-radius: 15px;
}

.starred-pages-wrapper {
    color: #D9D9D9 !important;
}

.starred-pages-wrapper .starred-pages .page {
    color: #D9D9D9 !important;
    padding-left: 10px;
}
.starred-pages-wrapper .starred-pages .page:hover {
    background-color: #14171C !important;
    border-radius: 15px;
}

.bp3-button[class*="bp3-icon-"]::before {
    color: #D9D9D9 !important;
}


/* Editor */
.roam-body-main {
    background-color: #000
}

.roam-topbar {
    background-color: #000;
    border-bottom: 1px solid #5C7080;
    box-sizing: border-box;
}
.roam-body .roam-app {
    color: #D9D9D9
}
.rm-reference-item {
    background-color: #14171C;
    border-radius: 5px;
}
.rm-title-display {
    color: #D9D9D9;
}
h1,
h2,
h3 {
    color: #D9D9D9 !important;
}
.rm-page-ref-tag {
    color: #5C7080
}
code {
    background-color: #13161C;
    color: #43A2A8;
    border: 1px solid #13161C;
    border-radius: 1px
}
.block-border-left {
    border-color: #5C7080
}
.check-container input:checked ~ .checkmark {
    background-color: #000
}
.roam-block > span > div {
    background-color: #13161C !important;
}
.rm-block-ref:hover {
    background-color: #182632 !important;
    border-radius: 2px !important;
}
.roam-highlight{
    color: #000 !important;
}
.block-highlight-blue{
    background-color: #4B7199 !important;
}

/* All Pages */
.rm-pages-row {
    background-color: #14171C !important;
    border-bottom: 1px solid #2F3336 !important;
}
.rm-pages-title-col > .bp3-text-overflow-ellipsis > a {
    color: #D9D9D9 !important;
}
.rm-pages-title-col > .bp3-text-overflow-ellipsis > span {
    color: #6E767D !important;
}


/* Right Side Bar */
#right-sidebar {
    background-color: #000 !important;
    border-left: 1px solid #5C7080;
    box-sizing: border-box;
}

/* Help Popup */
#buffer {
    background-color: #000 !important;
    box-shadow: rgba(255, 255, 255, 0.2) 0px 0px 15px, rgba(255, 255, 255, 0.15) 0px 0px 3px 1px;
}


.bp3-input {
    background: #FFFFFF;
    background: rgba(255, 255, 255, .05);
}
.bp3-elevation-3 {
    background-color: #000 !important;
    box-shadow: rgba(255, 255, 255, 0.2) 0px 0px 15px, rgba(255, 255, 255, 0.15) 0px 0px 3px 1px;
    color: #5C7080 !important;
}
.bp3-button.bp3-minimal:hover {
    background-color: #041019 !important;
}
.bp3-datepicker {
    background-color: #000 !important;
    color: #5C7080 !important;
}
.bp3-popover-content {
    background-color: #000 !important;
    color: #5C7080 !important;
}
.bp3-popover {
    box-shadow: rgba(255, 255, 255, 0.2) 0px 0px 15px, rgba(255, 255, 255, 0.15) 0px 0px 3px 1px;
}
.bp3-menu-item:hover {
    background-color: #14171C !important;
}
.bp3-menu {
    background-color: #000 !important;
    color: #D9D9D9 !important;
}
.bp3-popover-arrow-fill {
    fill: #000 !important;
}
.bp3-popover-arrow-border {
    fill: rgb(255, 255, 255) !important;
}
.bp3-datepicker {
    color: #fff !important
}
.bp3-html-select > select {
    color: #fff
}
.bp3-button {
    background-color: #12151B !important;
    background-image: none !important;
    color: #d9d9d9 !important;
}
.bp3-input {
    color: #d9d9d9 !important;
}
}
