css:
/*
    VERSION: 1.0.1
    AUTHOR: EvilSpark
  */
@-moz-document domain('wuxiaworld.com'), url-prefix('https://wuxiaworld.com') {

body {
	background: none !important;
	background-color: #282828 !important;
}

div[id*="announcement-"] {
	display: none !important;
}

#site-navigation {
	min-width: 1px;
}

#secondary,
div[role="complementary"],
#top-area,
#menu-item-12207,
#menu-item-2165,
#menu-item-116520,
#menu-item-166036,
#menu-item-43860,
#menu-item-9369,
#menu-item-34926,
/*}
  #menu-item-115,
  #menu-item-43860,
  #menu-item-41669,
  */
#subArea,
#footer-wide,
img[src*="title-icon"],
#widget_0,
#widget_24,
#widget_41,
#widget_1,
#widget_72,
.ad-bar-area,
.header,
.footer {
	display: none !important;
}

#page {
	padding: 0 6px 24px 6px;
	width: 100%;
}

#primary,
.content .row div {
	width: 100% !important;
}

.top-bar-area .caption h4 {
	font-size: 2rem;
}

article {
	width: 100% !important;
	padding: 0 12px !important;
}

p[style*="margin:0; font-size: 10px;"] {
	display: none;
}


/* NAVIGATIONS */
#navbar > ul.nav.navbar-nav.nav-public > li.hidden-xs,
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(3),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(4),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(5),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(6),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(7),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(8),
#navbar > ul.nav.navbar-nav.nav-public > li:nth-child(9) {
	display: none;
}

#sidebar {
	display: none;
}

#content-container {
	width: 100% !important;
}


@media (min-width: 1280px) {
	.container {
		width: 1000px;
	}
}}
