css:
@-moz-document url-prefix("https://streamelements.com/overlay") {
.oe-container, .editor.ui-flow-1, [class^=max-width-], #editor-wrapper, .editor .alert-box.layout--column.isHidden, .alert-box, .editor .overlay:after{
    background:#222!important;
    color:#fafafa!important
}
.se-label, ._md-nav-button-text{
    color:#fafafa!important
}
.widget-layers-list-item-header{
    background:#00332a!important;
    color:#fafafa!important
}
md-sidenav.md-closed, .se-widget-details-v3__header{
    background:#333!important;
    color:#fafafa!important
}
.md-subheader-inner, .md-chips.md-removable md-chip, md-menu-content, .upload-media .controls, .floating-actions__items, .advanced-settings-slide-up widget-details-title ~ form.expanded, .overlay-settings, .event-emulator__root:before, .video-volume-controls{
    background:#444!important;
    color:#fafafa!important
}
#widget-details, .widget-details, .widget-form, md-tabs.md-dynamic-height md-tab-content.md-active, .color-picker-wrapper .input-group .color-picker-input, .font-select-active>span, .font-select *, .widgets-list-item, .md-button{
    background:#353535!important;
    color:#fafafa!important
}
.md-input-container.md-default-theme .md-input, md-input-container .md-input{
    color:#fafafa!important
}
.hint{
    color:#d5d5d5!important
}
.md-input-container.md-input-focused label:not(.md-no-float), md-input-container.md-input-has-placeholder label:not(.md-no-float), md-input-container.md-input-has-value label:not(.md-no-float){
    color:#d5d5d5!important
}
.md-subheader .md-subheader-inner{
    background-color:#100033!important
}
.material-icons, .md-icon, .md-select-value .md-select-icon:after, .md-button.md-icon-button .material-icons, .md-button.md-icon-button md-icon, .md-datepicker-calendar-pane.md-pane-open, .md-button.layout-option[type="button"] img{
    filter:invert(100%)!important
}
.md-input-container .md-placeholder, md-input-container label:not(.md-no-float):not(.md-container-ignore){
    color:#fafafa!important
}
.md-tab{
    background:#6c6c6c!important;
    color:#fafafa!important
}
.md-tabs, .md-tab.md-active, .md-tab.md-active{
    background:#003057!important;
    color:#fafafa!important
}
md-tabs-canvas{
    background:#404040!important
}
.md-button.md-ghost.md-ghost-primary:hover, .md-button.md-ghost.md-primary:hover{
    background-color:rgba(87,135,220,.2)!important
}
.md-input-container.md-default-theme .md-input, md-input-container .md-input{
    background:#464646!important;
    border-radius: 5px;
    border: 1px solid #00a0a6
}
.md-select-menu-container.md-active md-select-menu>*, md-option .md-text{
    background:#555!important;
    color:#fafafa!important
}
md-divider{
    border-top-color:#99a4a8!important;
}
md-select.md-default-theme .md-select-value, md-select .md-select-value{
    border-bottom-color:#99a4a8!important
}
text-editor .md-tab:after, md-tabs md-ink-bar{
    background-color:#00fff3!important
}
.text-alignment .md-button.md-icon-button.active{
    background-color:#0d938c!important
}
md-dialog.md-transition-in, .event-emulator__preview-live__root{
    background:#002a55!important;
    color:#fafafa!important
}

/* THE WEIRD GREEN BACKGROUND */
.editor.ui-flow-1 .overlay{
    background-color:#5e5e5e!important
}
}
