input[type=radio] {
    position: relative;
    cursor: pointer;
    margin-right: 15px !important;
}

input[type=radio]:before {
    content: "";
    display: block;
    position: absolute;
    width: 16px;
    height: 16px;
    top: 0;
    left: 0;
    border: 1.5px solid #212223;
    border-radius: 3px;
    background-color: #212223;
       
}

input[type=radio]:checked:after {
    content: "";
    display: block;
    width: 5px;
    height: 10px;
    border: solid var(--MainThemeColor);
    border-width: 0 2px 2px 0;
    -webkit-transform: rotate(45deg);
    -ms-transform: rotate(45deg);
    transform: rotate(45deg);
    position: absolute;
    top: 2px;
    left: 6px;
}



::-webkit-scrollbar {
    width: 7px;
}
::-webkit-scrollbar-track {
    background: #00000061;
}
::-webkit-scrollbar-thumb {
    background: #77777763;
}
::-webkit-scrollbar-thumb:hover {
    background: #555;
}


/* Fullscreen */

.app {
    position: fixed !important;
    width: 100% !important;
    height: 100% !important;
    top: 0px !important;
    left: 0px !important;
    margin-top: 0px;
}

.pane-three {
    position: fixed !important;
    width: 300px !important;
    left: calc(100% - 300px) !important;
    z-index: 100 !important;
}

.pane-one {
    width: 33% !important;
    min-width: 280px !important;
}

.pane-two {
    width: 100% !important;
}
