css:
@-moz-document url-prefix("https://trello.com"), url-prefix("http://trello.com") {
#trello-root {
    background-image:none !important;
    background:-webkit-linear-gradient(90deg, #f0f0f0, #fff) !important;
}
.list {
    background:none !important;
}
.list-card {
    border:1px solid #f0f0f0;
    box-shadow:0px 5px 10px rgba(0, 0, 0, 0.1);
    padding:7.5px;
    border-radius:10px !important;
}
.list-cards {
    padding:0 8px;
}
.card-composer-container {
    width:90%;
    margin:auto;
    border:1px solid #f0f0f0;
    box-shadow:0px 5px 10px rgba(0, 0, 0, 0.1);
    background:#FFF;
    border-radius:10px !important;
}
.open-card-composer {
    display:flex !important;
    align-items:center !important;
    justify-content:center !important;
    margin:0 !important;
}
.card-composer-container .js-card-templates-button {
    display:flex !important;
    align-items:center !important;
    justify-content:center !important;
}
.list-card-title {
    font-family:Estedad, Yekan Bakh, dana, sans-serif, tahoma;
    padding-top:5px;
    padding-bottom:5px;
}
h1, h2, h3, h4, h5, h6, .list-header-name {
    font:initial !important;
    font-family:Estedad, Yekan Bakh, dana, sans-serif, tahoma !important;
    font-weight:400 !important;
    /*color:#3f539a !important;*/
}
.body-custom-board-background #header {
    background-color:#3857c5 !important;
}
#header button,#header a,#header input {
    background-color:rgba(255, 255, 255, .1);
    border-radius:10px !important;
}
#header>a:nth-child(2){
 background-color:transparent;   
}
.icon-sm {
    font-size:14px;
}
.badge-text {
    font-size:10px;
}
.card-label-green {
    background-color:#4dc377 !important;
}
.card-label-sky {
    background-color:#6d76e2 !important;
}
.card-label-yellow {
    background-color:#f1d54d !important;
}
.card-label-red {
    background-color:#fb7669 !important;
}
.u-fancy-scrollbar::-webkit-scrollbar {
    width:3px;
}
.list-header.is-menu-shown {
    width:75%;
}
.body-light-board-background .board-header-btn:not(.no-edit) {
    background-color:#FFF;
    border:1px solid #f0f0f0;
    box-shadow:0px 5px 10px rgba(0, 0, 0, 0.1);
    border-radius:10px !important;
}
.body-dark-board-background .board-canvas{
 	background:none !important;   
}
.body-dark-board-background .board-header{
 	background:rgb(69, 97, 197) !important;   
}
.list-wrapper.mod-add .open-add-list>span{
   	background:rgb(69, 97, 197) !important;   
    border-radius:7.5px;
    padding:10px;
    box-shadow:0px 5px 10px rgba(69,97,197,.3);
    font-size:12px;
    font-weight:300;
}
}
