@-moz-document domain("zoom.us") {
/* New Icons */

.glyphicon-question-sign:before,
#notice-close:before {
	content: '';
}

/* Checkmark _______________________________________________________________________________*/

.zm-icon-version:before {
    content: "";
}

.zm-tooltip.zm-button--icon {
    font-size: 33px!important;
}

/* Big Check ___________________________*/

.zm-icon-version:before
{
	background: url(https://i.ibb.co/ZMNmHwc/Mcrw-IRxnnrq.png) no-repeat!important;
	box-sizing: border-box;
}

.zm-icon-version:before {
    background-size: 31px!important;
    padding-left: 42px!important;
}

/* Checked ___________________________*/

.zm-checkbox__original:checked+.zm-checkbox__inner::before
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

.zm-checkbox__inner::before
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

.zm-checkbox__inner::before,
.zm-checkbox__original:checked+.zm-checkbox__inner::before
{
	background-size: 31px!important;
	height: 36px!important;
	padding-left: 32px!important;
	width: 1px!important;
}

/* Close _______________________________________________________________*/

#notice-close:before,
.zm-alert--warning .zm-alert__closebtn.zm-button,
i.status-icon
{
	background: url(https://i.ibb.co/kxQCnBQ/close-48x48.png) no-repeat!important;
	box-sizing: border-box;
}

#notice-close:before,
.zm-alert--warning .zm-alert__closebtn.zm-button,
i.status-icon
{
	background-size: 15px!important;
	height: 26px!important;
	padding-left: 22px!important;
	width: 10px!important;
}

/* Lights ______________________________________________________________________*/

.zm-switch__core:after {
    top: -2px;
}

.zm-radio__inner::before {
    left: -8px!important;
    top: 21%!important;
}

/* On ___*/

.zm-radio__original:checked+.zm-radio__label .zm-radio__inner::before,
.zm-switch__input:checked+.zm-switch__core::after
{
	background: url(https://i.ibb.co/njmsz2z/green-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ___*/

.zm-radio__inner::before,
.zm-switch__core:after
{
	background: url(https://i.ibb.co/z5C1bGj/red-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

.zm-radio__inner::before,
.zm-radio__original:checked+.zm-radio__label .zm-radio__inner::before,
.zm-switch__core:after,
.zm-switch__input:checked+.zm-switch__core::after
 {
    background-size: 28px!important;
    height: 32px!important;
    padding-left: 25px!important;
    width: 1px!important;
}

/* Question _______________________________________________________________*/

.glyphicon-question-sign:before {
	background: url(https://i.ibb.co/9V5P6z6/question-54x54.png) no-repeat!important;
	box-sizing: border-box;
}

.glyphicon-question-sign:before {
	background-size: 15px!important;
	height: 26px!important;
	padding-left: 22px!important;
	width: 10px!important;
}
}
