css:
/* background color 1 #121212 */
/* background color 2 #1F1F1F */

@-moz-document regexp("https://.*shopify.dev/.*"){

body, .grid__item--main, #main, main {
    background: #121212 !important;
}
div > main {
    background: #121212 !important;
}
#ShopifyMainNav {
    background:#1F1F1F !important;
}
.long-form-content p, article p, .long-form-content ul, article ul, .long-form-content ol, article ol {
    color: #fff !important;
}
h1, h2, h3, article h1, article h2 {
    color: #fff !important;
}
nav .popover-wrapper > button {
    color: #9bb2c5;
}
.sidebar-link {
    color: #9bb2c5;
}
.sidebar-submenu, .sidebar__subheading {
    color: #cbe4fd;
}
.sidebar-submenu.accordion-content.sidebar-submenu--active {
    background: black !important;
}
body .marketing-nav--skin-light .marketing-nav__item--primary {
    color: #9bb2c5;
}
.note {
    background: #0c0c0c !important;
}
td>code, td tt, p>code, p tt, ul>code, ul tt, ol>code, ol tt, li>code, li tt{
  background:#000639;
  border: none !important;
}
.sidebar-submenu.accordion-content{
  background: #1F1F1F !important;
}
.code-label{
  color: black !important;
}

.toc a:hover, .toc a:focus, .toc-accordion__content a:hover, .toc-accordion__content a:focus {
    color: #fdfdfd !important;
}

.toc-container .js-is-active {
    color: #87929c !important;
}
.marketing-nav__logo .icon{
filter: invert(1);
}
body .long-form-content h4{
  color: #93bee8;
}
body .tutorials-section ul a {
    color: #5e7994;
}

body .marketing-nav--skin-light .marketing-nav__item--primary:active, .marketing-nav--skin-light .marketing-nav__item--primary.marketing-nav__item--active{
    color: #a6d2ff;
}

.marketing-nav--skin-light .marketing-nav__item--user{
    color: #9accff;
}

main p, main ul, main ol{
    color: #bbbbbb;
}
body p.gutter-bottom--reset{
     color: #4a3d94 !important;
}
td{
     color: #efefef;
}
}
