css:
@-moz-document url-prefix("https://superium.net/") {
html {
    background-color: #2d2d2d !important;
    font-size: 16px;
    -moz-osx-font-smoothing: grayscale;
    -webkit-font-smoothing: antialiased;
    min-width: 300px;
    overflow-x: hidden;
    overflow-y: scroll;
    text-rendering: optimizeLegibility;
    -webkit-text-size-adjust: 100%;
    -moz-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    text-size-adjust: 100%;
}

.footer {
    background-color: #282828 !important;
    padding: 3rem 1.5rem 6rem;
}

.box {
    background-color: #2d2d2d !important;
    border-radius: 6px;
    box-shadow: 0 0.5em 1em -0.125em rgba(10,10,10,.1), 0 0 0 1px rgba(10,10,10,.02);
    color: #F7F7F7 !important;
    display: block;
    padding: 1.25rem;
}

.title {
    color: #fff !important;
    font-size: 2rem;
    font-weight: 600;
    line-height: 1.125;
}

.notification.is-warning {
    background-color: #272C54 !important;
    color: #fff !important;
}

.navbar {
    background-color: #2d2d2d !important;
    min-height: 3.25rem;
    position: relative;
    z-index: 30;
}

.navbar.has-shadow {
    box-shadow: 0 2px 0 0 #000 !important;
}

.navbar-item, .navbar-link {
    color: #fff !important;
    display: block;
    line-height: 1.5;
    padding: .5rem .75rem;
    position: relative;
}

.navbar-link.is-active, .navbar-link:focus, .navbar-link:focus-within, .navbar-link:hover, a.navbar-item.is-active, a.navbar-item:focus, a.navbar-item:focus-within, a.navbar-item:hover {
    background-color: #404040 !important;
    color: #fff !important;
}

.tag:not(body).is-danger {
    background-color: #272C54 !important;
    color: #fff !important;
}

.button {
    background-color: #2d2d2d !important;
    border-color: #000;
    border-width: 1px;
    color: #fff !important;
    cursor: pointer;
    justify-content: center;
    padding-bottom: calc(.5em - 1px);
    padding-left: 1em;
    padding-right: 1em;
    padding-top: calc(.5em - 1px);
    text-align: center;
    white-space: nowrap;
}

.dropdown-content {
    background-color: #2E2E2E !important;
    border-radius: 4px;
    box-shadow: 0 0.5em 1em -0.125em rgba(10,10,10,.1), 0 0 0 1px rgba(10,10,10,.02);
    padding-bottom: .5rem;
    padding-top: .5rem;
}

.subtitle {
    color: #fff !important;
    font-size: 1.25rem;
    font-weight: 400;
    line-height: 1.25;
}

a {
    color: #F2F2F2 !important;
    cursor: pointer;
    text-decoration: none;
}

.button.is-hovered, .button:hover {
    border-color: #000 !important;
    color: #fff !important;
}

a.dropdown-item:hover, button.dropdown-item:hover {
    background-color: #2E2E2E !important;
    color: #fff !important;
}

.navbar-dropdown {
    /* font-size: .875rem; */ 
    padding-bottom: .5rem !important;
    padding-top: .5rem;
    background-color: #2E2E2E;
    border-top: #000 !important;
}

.navbar-divider {
    background-color: #000 !important;
    border: none;
    display: none;
    height: 2px;
    margin: .5rem 0;
}

.navbar-link:not(.is-arrowless)::after {
    border-color: #F3F3F3 !important;
    margin-top: -.375em;
    right: 1.125em;
}

@media screen and (min-width: 1024px)
{navbar-item.has-dropdown.is-active .navbar-link, .navbar-item.has-dropdown:focus .navbar-link, .navbar-item.has-dropdown:hover .navbar-link {
    background-color: #282828 !important;
}}

.heading {
    display: block !important;
    font-size: 11px;
    letter-spacing: 1px;
    margin-bottom: 5px;
    text-transform: uppercase;
    color: #f3f3f3 !important;
}

.menu-list a.is-active {
    background-color: #1D1D1D!important
    ;color: #fff !important;
}

.menu-list a {
    border-radius: 2px !important;
    color: #F3F3F3 !important;
    display: block;
    padding: .5em .75em;
}

a:hover {
    color: #F3F3F3 !important;
}

.dropdown-item {
    color: #F3F3F3 !important;
    display: block;
    font-size: .875rem;
    line-height: 1.5;
    padding: .375rem 1rem;
    position: relative;
}

.input, .select select, .textarea {
    background-color: #8C8C8C !important;
    border-color: #3E3E3E !important;
    border-radius: 4px;
    color: #fff !important;
}

.input:hover, .is-hovered.input, .is-hovered.textarea, .select select.is-hovered, .select select:hover, .textarea:hover {
    border-color: #000 !important;
}

.table td, .table th {
    border: 1px solid #000 !important;
    border-width: 0 0 1px;
    padding: .5em .75em;
    vertical-align: top;
}

.table {
    background-color: #2E2E2E !important;
    color: #fff !important;
}

.table thead td, .table thead th {
    border-width: 0 0 2px !important;
    color: #fff !important;
}

strong {
    color: #f3f3f3 !important;
    font-weight: 700;
}

.button.is-info {
    background-color: #fff !important;
    border-color: transparent;
    color: #2d2d2d !important;
}

.button.is-success {
    background-color: #f3f3f3 !important;
    border-color: transparent;
    color: #2d2d2d !important;
}

.tag:not(body).is-success {
    background-color: #272C54 !important;
    color: #fff !important;
}

.tag:not(body).is-dark {
    background-color: #020237 !important;
    color: #fff !important;
}

.tag:not(body) {
    align-items: center;
    background-color: #020237 !important;
    border-radius: 4px;
    color: #fff !important;
    display: inline-flex;
    font-size: .75rem;
    height: 2em;
    justify-content: center;
    line-height: 1.5;
    padding-left: .75em;
    padding-right: .75em;
    white-space: nowrap;
}

.pagination-link, .pagination-next, .pagination-previous {
    border-color: #000 !important;
    color: #fff !important;
    min-width: 2.5em;
}

.select:not(.is-multiple):not(.is-loading)::after {
    border-color: #fff !important;
    right: 1.125em;
    z-index: 4;
}

.button.is-success.is-hovered, .button.is-success:hover {
    background-color: #B9B9FF !important;
    border-color: transparent;
    color: #fff !important;
}

.button.is-danger {
    background-color: #0B0B33 !important;
    border-color: transparent;
    color: #fff !important;
}

.modal-card-head {
    border-bottom: 1px solid #000 !important;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}

.modal-card-foot, .modal-card-head {
    align-items: center !important;
    background-color: #232323 !important;
    display: flex;
    flex-shrink: 0;
    justify-content: flex-start;
    padding: 20px;
    position: relative;
}

.modal-card-body {
    -webkit-overflow-scrolling: touch !important;
    background-color: #2d2d2d !important;
    flex-grow: 1;
    flex-shrink: 1;
    overflow: auto;
    padding: 20px;
}

.modal-card-foot {
    border-bottom-left-radius: 6px !important;
    border-bottom-right-radius: 6px;
    border-top: 1px solid #000 !important;
}

.modal-card-title {
    color: #fff !important;
    flex-grow: 1;
    flex-shrink: 0;
    font-size: 1.5rem;
    line-height: 1;
}

.button.is-success.is-hovered, .button.is-success:hover {
    background-color: #D1D1D1 !important;
    border-color: transparent;
    color: #000 !important;
}
}
