}
#internationalHeader .nav-search-box::before {
  content: '';
  position: absolute;
  top: 0px;
  left: -150px;
  background: url(https://i.loli.net/2020/03/23/pTaXQMB6yN9WHZL.png
    ) no-repeat !important;
