#internationalHeader .primary-menu-itnl #primaryChannelMenu span:nth-of-type(16),
#internationalHeader .primary-menu-itnl #primaryChannelMenu span:nth-of-type(17),
#internationalHeader .primary-menu-itnl #primaryChannelMenu span:nth-of-type(18),
#internationalHeader .primary-menu-itnl #primaryChannelMenu span:nth-of-type(19),
#internationalHeader .primary-menu-itnl #primaryChannelMenu span:nth-of-type(20) {
  display: none;
}
#internationalHeader .primary-menu-itnl #primaryChannelMenu .bili-icon_caozuo_xiangyou-copy {
  display: none;
}
#internationalHeader .primary-menu-itnl #primaryChannelMenu a span::after {
  content: '';
  width: 0;
  height: 0;
  font-size: 0;
  border-left: 5px solid transparent;
  border-right: 5px solid transparent;
  border-top: 5px solid #fff;
  margin: 5px 0 0 5px;
}
#internationalHeader .primary-menu-itnl #primaryFriendshipLink {
  display: none;
}
.van-popper-channel {
  border: 0;
}
.van-popper-channel .sub-container {
  background-color: #202020;
  color: white;
}
.van-popper-channel .sub-container a {
  color: white;
}
#van-popover-3759 .popper__arrow {
  left: 30px !important;
}
.proxy-box > div .storey-title .l-con a.name {
  color: black !important;
}
.proxy-box > div .storey-title .l-con a.name::after {
  float: left;
  color: white;
}
#bili_manga,
#bili_music,
#bili_dance,
#bili_game,
#bili_technology,
#bili_digital,
#bili_life,
#bili_kichiku,
#bili_fashion,
#bili_ad,
#bili_ent,
#bili_read,
#bili_movie,
#bili_teleplay,
#bili_cinephile,
#bili_documentary,
#bili_guochuang {
  display: none;
}
.proxy-box .storey-title .l-con a.name {
  color: white;
}
.proxy-box .storey-title .l-con svg {
  display: none;
}
.proxy-box .storey-title .l-con a.name:hover {
  text-decoration: underline !important;
}
.proxy-box .number.on {
  background-color: #f90 !important;
}
.proxy-box #bili_live .storey-title .l-con a.name::after {
  content: "直播视频";
}
.proxy-box #bili_live .tab-switch .on {
  color: white;
  border-bottom-color: #f90;
}
.proxy-box #bili_live .tab-switch .tab-switch-item:last-child {
  display: none;
}
.proxy-box #bili_live .text-info a {
  display: none;
}
.proxy-box #bili_live p.desc,
.proxy-box #bili_live .txt p.p2 {
  color: #cacaca;
}
.proxy-box #bili_douga .storey-title .l-con a.name::after {
  content: "热门片PORN";
}
.proxy-box #bili_douga .rank-wrap a {
  color: #cacaca;
}
.proxy-box #bili_anime .storey-title .l-con a.name::after {
  content: "观看最多";
}
.proxy-box #bili_anime .space-between:nth-of-type(2) .storey-title .l-con a.name::after {
  content: "为您推荐的分类 - 劲爆重口味";
}
#bili_report_spe_rec svg.svg-icon {
  display: none;
}
#bili_report_spe_rec .storey-title .l-con a.name {
  color: black;
}
#bili_report_spe_rec .storey-title .l-con a.name::after {
  float: left;
  color: white;
  content: "最新精选X级视频";
}
.international-footer {
  background-color: #151515;
  color: #cacaca;
  border-top: #292929 1px solid;
}
.international-footer a {
  color: #f90;
}
.btn,
.btn i,
a.more,
a.more i {
  color: white !important;
}

}

