@-moz-document url("https://www.bilibili.com/") {
/* miniheader  */
.nav-link a {
  color: white !important;
}
#internationalHeader {
  width: 1414px;
  margin: 0 auto;
}
@media screen and (max-width: 1654px) {
  #internationalHeader,
  .b-wrap {
    width: 1198px;
  }
}
@media screen and (max-width: 1438px) {
  #internationalHeader,
  .b-wrap {
    width: 999px;
  }
}
.mini-header__content {
  display: block;
  background: #1b1b1b;
  padding: 0px;
  height: 84px;
}
.mini-header__content .bili-icon_dingdao_zhuzhan,
.mini-header__content .bili-icon_dingdao_xiazaiapp {
  display: none;
}
.mini-header__content .nav-link {
  width: 100%;
  height: 22px;
  background: black;
}
.mini-header__content .nav-link a {
  color: #BBBBBB;
}
.mini-header__content .nav-link a:hover {
  color: white;
}
.mini-header__content .nav-link .nav-link-ul {
  height: 22px;
}
.mini-header__content .nav-link .nav-link-item {
  margin: 0;
  height: 22px;
  line-height: 12px;
}
.mini-header__content .nav-link .nav-link-item a {
  padding: 5px 24px;
  text-transform: uppercase;
  font-size: 11px;
}
.mini-header__content .nav-link .nav-link-item .bili-Navbar_logo {
  display: none;
}
.mini-header__content .nav-link .nav-link-item:hover {
  background-color: #333333;
}
.mini-header__content .nav-user-center {
  float: right;
  padding: 22px 0;
}
.mini-header__content .nav-user-center .mini-upload {
  width: 60px;
  height: 26px;
  position: absolute;
  right: calc(100% - 820px);
  bottom: 18px;
  line-height: 26px;
  color: #acacac;
  font-weight: bold;
  border-radius: 4px;
  background-color: #2f2f2f;
}
.mini-header__content .nav-user-center .signin {
  position: relative;
}
.mini-header__content .nav-user-center .signin .item:not(:first-child) {
  width: 25px;
  height: 18px;
  margin: 0 6px;
}
.mini-header__content .nav-user-center .signin .item:not(:first-child) span div.van-popover__reference span,
.mini-header__content .nav-user-center .signin .item:not(:first-child) .nav-item .t a span {
  opacity: 0;
}
.mini-header__content .nav-user-center .signin .item:first-child {
  position: absolute;
  right: 1vw;
}
.mini-header__content .nav-user-center .signin .item:first-child .van-popover__reference img {
  width: 39px;
  height: 39px;
  border-radius: 0;
}
.mini-header__content .nav-user-center .signin .item:nth-of-type(3) .t,
.mini-header__content .nav-user-center .signin .item:nth-of-type(4) .t {
  background: url(https://ci.phncdn.com/www-static/images/sprite-icons.png?cache=2020031801) no-repeat;
  background-position: -2px -495px;
}
.mini-header__content .nav-user-center .signin .item:nth-of-type(3) .t {
  background-position: -2px -495px;
}
.mini-header__content .nav-user-center .signin .item:nth-of-type(4) .t {
  background-position: -32px -495px;
}
.mini-header__content .nav-user-center .signin .item:nth-of-type(6) {
  background: url(https://ci.phncdn.com/www-static/images/sprite-icons.png?cache=2020031801) no-repeat;
  background-position: -61px -495px;
  margin-right: 70px;
}
.mini-header__content .nav-user-center .signin .item:last-child,
.mini-header__content .nav-user-center .signin .item:nth-of-type(2),
.mini-header__content .nav-user-center .signin .item:nth-of-type(5) {
  display: none;
}

}
