css:
@-moz-document regexp(".*/owa/.*"), regexp(".+/owa/.*(mail|calendar|people|tasks)") {
:root {
    --text-color: #ccc;
    --border-color: #5e747b;
    --darker-color: #312a31;
    /*--darker-color: #48454f;*/
    --dark-color: rgba(51,51,51,.75);
    --lighter-color: #86a6ce;
    
        --text-dim: #666;
    --text-bright: #ddd;
    --input-color: #313f448c;
    --interest: #383a53;
    --interest-hover: #595c84;
    --link-color: #9ac;
    --text-color: #bbb;
    --dark-color: #333;
}

 
 ::-webkit-scrollbar {
     width: 6px;
     height: 6px;
 }
 ::-webkit-scrollbar-thumb {
     background: var(--lighter-color);
     border-radius: 0px;
 }
 ::-webkit-scrollbar-track {
     background: var(--dark-color);
     border-radius: 0px;
 }

*
{
    background-color: transparent;
    border-color: var(--border-color);
}

/* scroll */

#divScrollbar
{
    width: 6px !important;
}
#divViewport
{
    right: 7px;
}

/* dividers */

#divNavDivideTile
{
    display:none;
}

.lvDivider
{
    width: 10px
}

#divNavDivide
{
    border-left: 1px solid var(--border-color);
}
#divNavDivide img {
    display:none !important;
}

/* options / context */

#divOptionsContextMenu
{
    border-color: var(--border-color);
    background-color: var(--dark-color);
}
.mnuItmTxtHdr  {
    color: var(--text-color);
}
.spnT, .cmLnk
{
    color: var(--link-color) !important;
}
#leftarrow.cmDsbld > img, #rightarrow.cmDsbld >img
{
    background-color: transparent !important;
}

/* popups? right click */

.ctxMnuIco > #cmpane
{
background: transparent;
}
.ctxMnuIco
{
    border-color: var(--border-color);
    background-color: var(--dark-color);
}
div.ctxMnu div.sttc
{
    border-color: transparent;
    background-color: var(--dark-color);
    color: var(--text-color);
}
.cmLnk.cmHiLite, .cmHiLite {
    background-color: var(--interest-hover);
    color: #000;
    border-color:  var(--border-color);
}

/* combos */

.dropDownListMaxHeight {
    background: var(--interest) !important;
    background-color: var(--interest) !important;
}
.cmbSel
{
    background-color: var(--interest-hover);
}
.dropDownListMaxHeight span {
       color: var(--text-color);
}

/* email edit / reply */


font[color="#000000"]
{
    color: var(--text-dim);  
}
input,
.fbBiu,
body, .MsoNormal ,
font[color="#000000"] b
{
    color: var(--text-color) !important;  
}
.fbBtnGrp
{
    border-right-color: var(--border-color);
}
.cmb
{
    filter: invert(80%);
}
#divFmtBr
{
    background-color: transparent;
    border-color: var(--border-color);
}

div#divToolbarButtonmailTips.tbf.lowSeverity
{
    border-color: var(--border-color);
}
div#divToolbarButtonmailTips.tbf.lowSeverity,
input { 
    background: transparent;
}
div.rwW, input.edtSubj,
.txtField
{
    border-color: var(--border-color);
    background-color: var(--input-color);
}
span.rwRR, span.rwUR
{
    background-color: var(--interest);
}
img {
     filter: 
        invert(100%)
        hue-rotate(180deg)
        grayscale(35%)
        sepia(10%);
}

div[style="margin:0;"] font span img {
     filter: invert(92%) hue-rotate(180deg) !important;
}

/* general */

#imgLiveLogo 
{
    filter: invert(40%) !important;
}

.subjLTxtLabel, .txtLabel
{
    color: var(--link-color);
}

[style*="background-color:white"]
{
    background-color: transparent !important;
}
.rwRRO,
font[color="black"] {
    color: var(--text-bright) !important;
}
#divExp > .divPrtBkSel,
#divHeaderDivideTile
{
    background: transparent !important;
}
span[style*="#0E406A"]
{
    color: var(--border-color) !important;
}

span[style*="color:black"],
font[color="#1F497D"],
span[style*="#1F497D"],
#divImgFlt,
[style*="margin:0;"] 
{
    color: var(--text-color) !important;   
}
body,
#divBackgroundImage
{
    background-color: var(--dark-color) !important;
}

.csimg.csimgbg.sprites-VLVShadowTop-png,
#divInfobarColor
{
    display: none;
    border-color: var(--border-color);
    background: transparent !important;;   
}
div#divIB
{
    border-top: 1px solid var(--border-color) !important;
    border-color: var(--border-color);
    background: var(--dark-color);
}
/*#divNavHostBody,*/
.lvContainer,
div.iactv,
#divMbxQtRg,
#divSecNv, #divSecNvAD, #divSecNvC
{
    background-color: var(--dark-color) !important;
}

.subMenu {
    background: var(--dark-color) !important;
}

.trDefSel {
    background: #555;
}

#spnUC #spnCV
{ 
    color : #a0a0c6;   
}

#spnC #spnCV
{
    color : #27971c;
}

#tdSO.v.r, #tdAB,
#divMsgItemTB div a,
#divTrNdO a,
a {
    color: #9ac !important;
}
/* inbox pane */


#divSScp:hover,
.tbfHvr:hover
{
    
    background: var(--interest-hover);
    border-color: var(--border-color);
}

div#divSearch
{
    border-color: var(--border-color);
}
div#divSearch > div#divTxt
{
    background: var(--interest);
}
input {
    background: transparent !important;
}

div#divSearch
{
    background: transparent;
}
div.iactv {
    border-color: transparent;
}

/* mail items area */

.lvContainer {
    border-top-color: var(--border-color);
    border-right-color: var(--border-color);
}

#divVLV
{
    background-color: var(--dark-color) !important;
}

#divSubject.c3 {
    color: var(--text-color) !important;
}
#divSubject.c3.ur { 
    color: var(--text-bright) !important;
}

.selBg { 
    opacity: .4;    
    filter: invert(85%);
}

/* divider list vs button left */
#divCatGroup:hover,
#aNbTgl:hover
{
    background: var(--interest-hover);
}
#aNbTgl
{
    background: var(--interest);
}

/* menu items to the left */

#divNvPn
{

    border-top-color: var(--border-color);
}

.nbMnuItm.nbMnuItmN span
{
    color: var(--text-color);
}
.nbNoHiLt, .nbWunderBar,
.nbHiLt {
    background: #444 !important;
}
#divBackgroundImageMain
{
    filter: invert(80%)
}
