    background: #0000 url(https://mail.wardjet.com/owa/headerbgmain.png) no-repeat left top;
} */


/* notification dialog */

.divRemindersSnoozeGradient,
.alertPopupShading,
.notificationsDialogTitle 
{
    background: transparent;    
}
.notificationsDialog
{
    background: #444 !important;
}
}
