css:
@-moz-document domain("nightbot.tv") {

/* default background color. */
.white-bg {
    background-color: #1d282d !important; 
}

/* breadcrumb */
.breadcrumb {
    background-color: #1d282d !important;
    padding: 0;
    margin-bottom: 0;
    border-radius: 2px;
}

/* navbar fix */

.navbar-static-top, .white-bg {
    background-color: #1d282d !important;
}

/* body + text */

body {
    background: #2f4050 !important;
    background-color: #2f4050 !important;
    color: #9da3a6;
    border: 1px solid #2f4050;
}

/* Button stuff */

.btn-default {
    background: #2f4050;
    background-color: #2f4050;
}

/* more background crap */
.gray-bg {
    background-color: #26323e;
    padding: 1px 1px 1px;
}

.ibox-title, .ibox-content {
    background-color: #2f4050;
    color: inherit;
    border-color: #1d282d;
    border-style: solid solid solid;
    padding: 1px 0px 1px;
}

.ibox-content {
    padding: 10px 10px 10px;
    height: 100%;
}

/* padding */

.col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
    padding: 1px 15px 1px;
}

/* fix borders */
.border-bottom {
    border-bottom: 1px solid #292e31 !important;
}
.wrapper {
    padding: 0px 0px 10px;
}

/* font fix */
.help-block {
    color: #9da3a6;
}

/* Search bar colors and formatting */

.form-control, .singl-line {
    background-color: #1c1c1c87;
    border: 1px solid #9da3a6;
    color: #9da3a6;
    padding: 1px 12px;
}

/* page footer */

.footer {
    background: 0 0 #1c1c1c87;
    color: #9da3a6;
    border: 1px solid #2f4050
}

/* hovering over stuff */

.project-list table tr td {
    border-top: none;
    border-bottom: 1px solid #9da3a6;
    padding: 15px 10px;
    vertical-align: middle;
}

.table-hover>tbody>tr:hover {
    background-color: #1d282d;
    color: #9da3a6;
}

.table-bordered>thead>tr>th, .table-bordered>thead>tr>td {
    background-color: #1d282d;
    border: 1px solid #2f4050;
    color: #9da3a6;
}

.table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td {
    border-top: 1px solid #9da3a6;
    line-height: 1.42857;
    padding: 8px;
    vertical-align: top;
}
}
