css:
@-moz-document domain(trello.com){
  .list-wrapper.mod-add .list-name-input{
    direction: rtl;
  }
  .list-header textarea{
  	direction: rtl;
  }
  textarea.list-card-composer-textarea, textarea.list-card-composer-textarea:focus{
    direction: rtl;
  }
  .dialog-close-button{
    left: 0;
    right:auto;
  }
  .card-label.mod-card-front{
    float: right;
    direction: rtl;
    margin: 0 0 4px 4px;
  }
  .list-card-operation{
    left: 2px;
    right: auto;
  }
  .window-header{
    direction: rtl;
    text-align: right;
    margin: 12px 56px 8px 40px;
  }
  .card-detail-item{
    float: right;
    margin: 0 0 8px 8px;
  }
  .window-module-title.description-title .button.subtle{
    margin: 0 8px 0 0;
  }
  .window-main-col{
    float: right;
    direction: rtl;
    text-align: right;
    padding: 0 16px 8px 8px;
  }
  .window-sidebar{
    float: left;
    padding: 0 8px 8px 16px;
  }
  .window-header-icon{
    right: -40px;
    left: auto;
  }
  .window-module-title-icon{
    right: -40px;
    left: auto;
  }
  .window-module-title{
    margin: 0 40px 4px 0;
  }
  .card-detail-item-header{
    margin: 0 0 4px 8px;
  }
  .u-gutter {
    margin-right: 40px;
    margin-left: 0;
  }
  input.mod-submit-edit {
    float: right;
    margin: 0 0 0 4px;
  }
  .window-module-title-options {
    float: left;
    margin-left: 2px;
    margin-right: 0;
  }
  .description-edit .helper{
    left: -2px;
    right: auto;
  }
  .checklist-title .window-module-title-options {
    margin: 0 auto 0 2px;
  }
  .window-module-title-options  button.subtle{
    margin: 0 0 0 8px;
  }
  .window-module-title-options  button.subtle:last-child{
    margin: 0;
  }
  .checklist-item{
    padding-right: 40px;
  }
  .checklist-item-checkbox{
    right: 0;
    left: auto;
  }
  .new-comment{
    margin: 0 40px 8px 0;
  }
  .new-comment .member{
    right: -40px;
    left: auto;
  }
  .member{
    margin: 0 0 4px 4px;
    float:right;
  }
  .comment-box-input{
    direction: rtl;
    text-align: right;
  }
  .phenom{
    margin-right: 40px;
    margin-left: 0;
  }
  .phenom-creator, .phenom-icon{
    right: -40px;
    left: auto;
  }
  .action-comment{
    margin: 4px 0 4px 2px;
  }
  .action-comment .current-comment {
    direction: rtl;
    text-align: right;
}
  blockquote{
    border-right: 1px solid rgba(9,30,66,.13);
    border-left: 0;
    margin: 12px 11px 12px 0;
    padding: 0 12px 0 0;
  }
  .phenom-reactions{
    margin-left: 24px;
    margin-right: 0;
  }
  .description-content h2{
    font-size: 18px;
     text-decoration: underline;
  }
}
