css:
@-moz-document domain("console.cloud.google.com") {
::-webkit-scrollbar {
    width: 0px;  /* Remove scrollbar space */
    background: transparent;  /* Optional: just make scrollbar invisible */
}
/*position indicator*/
::-webkit-scrollbar-thumb {
    background: #464646;
}
p {
    color: rgba(255, 255, 255, 0.654);
}
h1,h2,h3,h4,h5,h6,label,legend {
    color: white;
}
/*fondo base*/
.p6n-vulcan-panel-primary, .p6n-vulcan-panel-primary md-toolbar.md-menu-toolbar,/*lista de instancias*/
.p6n-default-theme, .central-page.p6n-default-theme, /*bd*/
body,
.cfc-panel-body-scroll-content, /*main*/
.cfc-ng2-region .cfc-background-base,
/*.dashboard-page--scrollable-content-container, .dashboard-page--flex-one,*/ /*stackdriver*/
.cfc-panel-color-white,
/*deployment manager*/
md-toolbar.md-menu-toolbar.p6n-mp-solution-detail-action-bar,
.cfc-panel-color-grey{
    background-color: #0a0a0a !important;
}
/*segunda barra*/
md-toolbar.md-default-theme.md-menu-toolbar, md-toolbar.md-menu-toolbar,
.cfc-ng2-region .cfc-background-contrast{
    background-color: rgb(51, 51, 51);
    color: rgba(255, 255, 255, 0.87);
}
.p6n-vulcan-toolbar {
    border-bottom: 1px solid #101010;
}
/*iconos*/
md-icon svg,
jfk-button svg{
    fill: white;
}
[disabled] md-icon svg{
    fill:#909090;
}
/*sidenav*/
/*top*/
mat-toolbar, mat-sidenav{
    background: #464646 !important;
}
.deferred-section-nav .cfc-section-header-content .cfc-section-name, .deferred-section-nav .cfc-section-header-content .cfc-section-category-name,
mat-sidenav .section-nav-sub-header{
    color: rgba(255, 255, 255, 0.655)!important;
}
.cfc-section-nav-body{
    background-color: #2f2f2f;
}
mat-sidenav{
	border-color: #1b1b1b;
}
mat-icon svg{
 	fill: white;
}

a.mat-button.gm1-section-nav-item {
    color: rgba(255, 255, 255, 0.655) !important;
}
/*lista de instancias*/
/*buscador*/
.p6n-chips ul {
    border: 1px solid #2f2f2f;
    background: #101010;
}
.p6n-chips li {
    color: rgba(255, 255, 255, 0.987);
}
.p6n-chip-control .button-remove svg {
    fill: rgba(255, 255, 255, 0.38);
}
/*head*/
.p6n-table th {
    color: rgba(255, 255, 255, 0.654);
}
.p6n-table th a.p6n-sort-link {
    color: white;
}
.p6n-table a {
    color: rgba(255, 255, 255, 0.987);
}
.p6n-table td {
    color: rgba(255, 255, 255, 0.654);
    border-bottom: 1px solid rgba(255, 255, 255,.08);
}
.p6n-table:not([disable-alternating-background]) tbody tr:nth-child(odd) {
    background-color: #2d2d2d;
}
/*botones*/
.modal-dialog-buttons .p6n-modal-action-button, .jfk-button, .jfk-button-standard {
    color: rgba(255, 255, 255, 0.987);
}
.jfk-button-flat:hover, .p6n-flat-dropdown:hover .goog-flat-menu-button, .p6n-split-dropdown-flat:hover .jfk-button, .p6n-split-dropdown-flat:hover .goog-flat-menu-button {
    background-color: #444444;
    border-color: #0e0e0e;
}
a.jfk-button {
    color: rgba(255, 255, 255, 0.987);
}
.modal-dialog-buttons .p6n-modal-action-button:disabled, .jfk-button:disabled, .jfk-button.jfk-button-disabled, .goog-flat-menu-button.goog-flat-menu-button-disabled {
    color: rgba(255, 255, 255, 0.654)!important;
}
pan-action-bar-button button.md-button.md-primary[aria-disabled="true"], md-menu-item.p6n-action-bar-overflow-item button.md-button.md-primary[aria-disabled="true"] {
    color: rgba(255, 255, 255, 0.38);
}
.cfc-ng2-region .mat-button, .cfc-ng2-region .mat-icon-button {
    color: white;
}
/*stackdriver*/
/*cards*/
.mat-card,
.cfc-info-card .cfc-info-card-styling,
.p6n-card-component,
.p6n-card{
    background: #252525 !important;
    color: rgba(255, 255, 255, 0.87) !important;
    border: 1px solid #151515;
}
.chart-card, mat-card.chart-card {
    border: 1px solid #0e0e0e !important;
}
/*filter*/
.sd2-dashboard-filter .ac-filter-selector {
    background-color: #232323 !important;
}
.cfc-ng2-region input[type=text], .cfc-ng2-region input[type=text]:hover, .cfc-ng2-region input[type=text]:focus, .cfc-ng2-region input[type=text]:disabled, .cfc-ng2-region input[type=password], .cfc-ng2-region input[type=password]:hover, .cfc-ng2-region input[type=password]:focus, .cfc-ng2-region input[type=password]:disabled, .cfc-ng2-region textarea, .cfc-ng2-region textarea:hover, .cfc-ng2-region textarea:focus, .cfc-ng2-region textarea:disabled {
    color: white;
}
.mat-input-element {
    background: transparent;
    color: white;
}
.mat-form-field-appearance-outline .mat-form-field-outline-start, .mat-form-field-appearance-outline .mat-form-field-outline-end {
    border: 1px solid #868686;
}
/*front page*/
div.cfc-info-text,
.cfc-info-card-header .cfc-info-card-header-text,
.cfc-ng2-region p{
    color:white !important;
}
.mat-tab-link[aria-disabled="false"]:not([aria-current="page"]){
	color: #e8e8e8 !important;
}
/*left bar*/
.cfc-ng2-region .cfc-text-title-5, .cfc-ng2-region .cfc-text-body-large {
    color: white;
}
.category-title,
.cfc-nav-products-list-header > button{
    color: rgba(255, 255, 255, 0.66);
}
/*instance view*/
.pcc-content-viewport[_ngcontent-iio-c18] {
    color: rgba(255, 255, 255, 0.87);
}
.p6n-checkbox.p6n-disabled span.p6n-form-label, .p6n-radio.p6n-disabled span.p6n-form-label {
    color: rgba(255, 255, 255, 0.38);
}
.p6n-compute-detail-label,
.p6n-tag-value {
    color: rgba(255, 255, 255, 0.654);
}
.p6n-tag {
    background-color: rgba(255, 255, 255, 0.08);
    color: rgba(255, 255, 255, 0.987);
}
.p6n-kv-list-item:nth-child(odd) {
    background-color: #2d2d2d;
}
.p6n-kv-list-key,
.p6n-multicol-stack-col{
    color:white;
}
.goog-tab-bar .goog-tab g-tab, .p6n-tab-bar .goog-tab a {
    color: rgba(255, 255, 255, 0.654);
}
.p6n-checkbox span.p6n-form-label, .p6n-radio span.p6n-form-label {
    color: rgba(255, 255, 255, 0.987);
}
/*vpc*/
.p6n-table tbody tr.p6n-compute-gray-row {
    background-color: #313131;
}
.p6n-table tbody tr.p6n-compute-white-row {
    background-color: #191919;
}
/*disctint table in internal ip add*/
cfc-table {
    background: #292929;
}
cfc-table .cfc-table-column-sortable {
    color: white;
}
cfc-table td {
    color: rgba(255, 255, 255, 0.66);
}
cfc-table tr {
    border-bottom: 1px solid #191919;
}
cfc-table tbody a, cfc-table tbody a .cfc-external-link-content, cfc-table tbody a:visited, cfc-table tbody a:visited .cfc-external-link-content, cfc-table tbody a:visited .ace-icon svg {
    color: #fff;
}
/*marketplace*/
mp-solution-detail-content p, mp-solution-detail-content .p6n-mp-solution-detail-content-div {
    color: rgba(255, 255, 255, 0.987);
}
.p6n-mp-solution-detail-banner {
    background-color: #272727;
}
.p6n-text-display,
.p6n-mp-details-text-big-base, .p6n-mp-details-text-big-base p,
.p6n-color-primary,
pan-form-list-item-summary-header,
.ace-kv-list>div dt,
.cfc-message{
    color: white !important;
}
.p6n-color-secondary {
    color: rgba(255, 255, 255, 0.654)!important;
}
.pcc-content-viewport {
    color: rgba(243, 243, 243, 0.87) !important;
}
.p6n-external-link .p6n-external-link-icon span, .p6n-external-link .p6n-external-link-icon span:before, .p6n-external-link .p6n-external-link-icon span:after {
    border-color: rgba(255, 255, 255, 0.54);
}
/*create deployment*/
input[type=text],
.modal-dialog-buttons .p6n-modal-action-button, .jfk-button-standard, .goog-flat-menu-button,
.jfk-textinput, textarea,
.p6n-form-list-item-summary{
    background: #1d1d1d;
    border: 1px solid #424242;
    color: #fbfbfb;
}
/*detail of deployment*/
.cfc-ng2-region .cfc-color-primary {
    color: white!important;
    fill: white!important;
}
.cfc-ng2-region .cfc-color-secondary {
    color: rgba(255, 255, 255, 0.66)!important;
    fill: rgba(255, 255, 255, 0.66)!important;
}
.ace-kv-list>div dd {
    color: rgba(255, 255, 255, 0.66);
}
.cfc-ng2-region h3,.cfc-ng2-region h4,.cfc-ng2-region .cfc-text-body-strong,
ul.ace-list{
    color: white;
}
}
