css:
@-moz-document domain("userstyles.org") {
/* main background */
#nav-view-responsive {
    background-color: #edeef0;
}

 /* scrollbar */
::-webkit-scrollbar-track {
  border-radius: 10px;
  background: rgba(0,0,0,0.1);
  border: 1px solid #ccc;
}
::-webkit-scrollbar-thumb {
  border-radius: 10px;
  background: linear-gradient(left, #fff, #e4e4e4);
  border: 1px solid #aaa;
}
::-webkit-scrollbar-thumb:hover {
  background: #fff;
}
::-webkit-scrollbar-thumb:active {
  background: linear-gradient(left, #22ADD4, #1E98BA);
}
 /* topbar */
#header {
    height: 42px;
    box-shadow: none;
    border-bottom: 1px solid #1c262d;
}
.header-search .type_search .type_search_select .type_search_option:hover {
    color: #000;
    background: none;
}
.logo {
    height: 24px;
    width: 79px;
}
.header-search {
    height: 28px;
    line-height: 17px;
    border-radius: 14px;
    margin: 9px 0;
}
.header-search .type_search .type_search_text {
    font-size: 12px;
    color: #ececec;
}
.header-search .search_input .input_text_search {
    font-size: 12px;
}
#create_new_style {
    font-size: 11px;
    border: none;
    box-shadow: none;
    width: 131px;
    height: 26px;
    font-weight: 500;
    min-height: 28px;
    -webkit-font-smoothing: subpixel-antialiased;
    text-transform: uppercase;
    border-radius: 4px;
    background-color: #3498db;
    padding: 0px 0px;
    margin-top: 10px;
    color: #fff !important;
}
#create_new_style:hover {
    border: none;
    color: #ffffff!important;
    background-color: #46adf2;
}
.header_button {
    font-size: 13px;
    color: #fff;
    font-weight: 500;
}
#top_android_button {
    display: none!important;
}

 /* navbar  */
.navigation {
    background-color: #fff;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}

@media screen and (min-width: 801px) {
.navigation .links-box .navigation-link .navigation-title {
   font-size: 13px;
    height: 14px;
} 
#nav-view-responsive {
    padding-top: 41px;
    padding-bottom: 41px;
}
.navigation {
    margin-top: 42px;
    padding: 15px 0px 20% 17px;
    width: 205px;
}
}
.navigation .links-box {
    box-shadow: 0 0px 0 0 #e7e8ec, 0 1px 0 0 #e7e8ec;
}
.navigation .links-box .navigation-link {
    box-shadow: none;
    padding: 6px 0 12px 0;
}

.navigation .navigation-category {
    font-size: 12px;
    color: #777;
    margin: 11px 0 10px 0;
}
 /* style item  */
#style_info {
    background: #fff;
    border-radius: 4px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    margin: 15px 0 0;
    padding: 0px 20px 20px 20px;
}
.style_title {
   font-size: 21px;
    font-weight: 700;
    -webkit-font-smoothing: subpixel-antialiased;
}
#notes_textarea {
    height: 315px;
    margin-top: 20px;
    padding: 15px;
    width: 698px;
    font-size: 13px;}

.author-name {
        font-size: 13px;
}
.by-author {
       font-size: 13px;
    color: #939393;
}
#preview_image_div {
    border-radius: 4px;
}
#share_div {
    font-size: 13px;
    color: #939393;
}
#donation_button {
background-color: #fff;
    box-shadow: none;
    border: none;
    height: 30px;
    padding: 0 10px;
      font-size: 12px;
}
.donation-icon {
    height: 17px;
    margin-top: 2px;
}
#actions_div {
    margin-top: 10px;
}
.customize_button { 
background-color: #fff;
    box-shadow: none;
    border: none;
    height: 30px;
    padding: 0 10px;
     font-size: 12px;
}

.social-network-logo {
    width: 16px;
    height: 16px;
}
.white_button {
    background-color: #fff;
    box-shadow: none;
    border: solid 1px #d7d8d9;
    height: 30px;
    padding: 0 10px;
    font-size: 12px;
}
.white_button:hover {
    background-color: #f8f8f8;
    box-shadow: none;
}
#install_style_button {
    box-shadow: none;
    font-size: 12px;
    width: 136px;
    height: 31px;
    font-weight: 500;
    -webkit-font-smoothing: subpixel-antialiased;
    text-transform: uppercase;
}
#style_installed_button {
box-shadow: none;
    font-size: 12px;
    width: 155px;
    height: 31px;
    font-weight: 500;
    -webkit-font-smoothing: subpixel-antialiased;
    text-transform: uppercase;
  background-color: #2ecc71;
    color: #fff;
}
#update_style_button {
box-shadow: none;
    font-size: 12px;
    width: 155px;
    height: 31px;
    font-weight: 500;
    -webkit-font-smoothing: subpixel-antialiased;
    text-transform: uppercase;
    background-color: #2ecc71;
    color: #fff;
}

#install_style_icon {
    margin-right: 4px;
    margin-top: 2px;
    width: 24px;
    height: 17px;
}
.update-icon {
    margin-right: 4px;
    margin-top: 2px;
    width: 24px;
    height: 17px;
}
.installed-icon {
    margin-right: 4px;
    margin-top: 2px;
    width: 24px;
    height: 17px;
}
#ownedButtons {
    margin-top: -1px;
}
#information_key_left {
    font-size: 13px;
    color: #828282;
}
#infomation_value_left {
    font-size: 13px;
    font-weight: 400;
}
#left_pair_information {
    margin-top: 11px;
}
#applies_link {
    font-size: 13px;
}
#right_information {
    padding-right: 30px;
}
.text_style_page {
    font-size: 13px;
}
.css_button {
    width: inherit;
    height: inherit;
    padding: 7px 16px 8px;
    font-size: 13px;
    font-weight: 500;
}
.css_close:after {
    content: "üìñ";
    margin-right: 10px;
    font-size: 15px;
}
.css_close {
    padding-bottom: 2px;
}
#archive_style_button {
    box-shadow: none;
    width: 106px;
    padding: 7px 16px 8px;
   font-size: 13px;
    font-weight: 500;
    color: #ec6066;
    border: solid 1px #ec6066;
}
#send_feedback_button {
   box-shadow: none;
    width: 120px;
    padding: 7px 16px 8px;
    font-size: 13px;
    font-weight: 500;
    height: 17px;
}
.feedback-icon {
    margin-top: 5px;
}
#stylish-code {
    font-family: Consolas;
}
 /* footer  */
#footer-inside {
    width: auto;
    margin-left: 0px;
}
#footer {
    background-color: inherit;
    font-size: 11px;
    width: 100%;
    min-width: 800px;
    display: -ms-flexbox;
    display: flex;
    -ms-flex-pack: center;
    justify-content: center;
    border-top: 1px solid var(--footerBorderColor);
    height: var(--footerHeight);
    margin: calc(var(--footerHeight) * -1) -20px 0;
}
#rightFooter {
    margin-left: 34px;
}
 /* cards */
.us-stylecard--short .us-username {
    color: #939393;
    margin-top: 4px;
    font-size: 12px;
    margin-bottom: 20px;
}
.us-stylecard--short .us-downloads__count {
    font-weight: 400;
      color: #939393;
}
.us-stylecard--short .us-discussions__count {
    color: #939393;
    font-weight: 400;
}
.us-stylecard--short .us-rating {
    margin-top: 18px;
}
.us-stylecard--short  {
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}
.us-stylecard--short .us-title {
    font-size: 14px !important;
}
.us-stylecard--short .us-updated {
    font-size: 10px;
 }
.us-stylecard--short .us-username a {
    color: #3498db;
}
.us-stylecard--short:hover {
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}
.us-stylecard--short .us-stencil {
    background-color: #fbfbfb;
    border-bottom: 1px solid #eff4f8;
    box-shadow: none;
}
 /* index */
.see_more {
    width: 92px;
    height: 27px;
    font-size: 12px;
    font-weight: 500;
}
#wrapper-carousel {
    background: #fff;
    border-radius: 4px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}
#overlay-info{
    border-radius: 0px;
}
#title_div h1 {
    -webkit-font-smoothing: antialiased;
    font-size: 40px;
    font-weight: 600;
}
#title_div {
    box-shadow: none;
}
#style-info-title-carousel {
    font-size: 22px;
    -webkit-font-smoothing: antialiased;
    font-weight: 600;
    color: #2c3e50;
}
.us-stylecard--short .us-username a {
    color: #3498db!important;
}
 /* styles cat*/
.StylesCategory h1 {
-webkit-font-smoothing: antialiased;
    font-size: 40px;
    font-weight: 600;
}
.style_category_card_wrapper {
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
}
#styles {
    margin-top: 10px;
    font-size: 11px;
    font-weight: 400;
}
 /* modal */
.choose_editor .boxesContainer .box .cta {
    box-shadow: none;
}
.main_title {
-webkit-font-smoothing: antialiased;
    font-size: 40px;
    font-weight: 600;
}
.choose_editor .boxesContainer {
    margin-top: 95px;
}

.choose_editor .boxesContainer .box .title {
-webkit-font-smoothing: antialiased;
    font-size: 27px;
    font-weight: 600;
}
.choose_editor {
top: 44px;
}
.overlay_background { opacity: 0!important;display: none!important; }
}
