@-moz-document domain("soundgasm.net") {
    html,
    body {
        background-color: #222;
    }
    body > .jp-audio {
        margin: 2em auto 0 auto;
        border-color: #eee;
    }
    body > div:nth-child(2),
    nav {
        text-align: center;
    }
    body > div:nth-child(2) > a {
        font-size: 1.5em;
    }
    body a {
        color: white;
    }
    header {
        min-height: 2.5em;
    }
    header > .logo {
        display: none;
    }
    .jp-play-bar,
    .jp-volume-bar-value {
        background: #484848;
    }
    .jp-seeking-bg {
        background: repeating-linear-gradient(120deg, #222, #eee, #222 9%);
    }
    .jp-description > p {
        padding: 1em;
        background: #222;
        color: white;
        margin: 0;
    }
    .jp-title {
        background-color: #484848;
        color: white;
    }
    /* Play */
    .jp-play,
    button.jp-play:focus {
        background: url(https://image.flaticon.com/icons/svg/254/254434.svg) center / 40% no-repeat;
    }
    .jp-state-playing button.jp-play,
    .jp-state-playing button.jp-play:focus {
        background: url(https://image.flaticon.com/icons/svg/633/633940.svg) center / 40% no-repeat;
    }
    /* Stop */
    .jp-stop,
    button.jp-stop:focus {
        background: url(https://image.flaticon.com/icons/svg/254/254443.svg) center / 40% no-repeat;
    }
    /* Play+Stop */
    div.jp-controls > button {
        border: 1px solid #222;
        border-radius: 8px;
    }
    .jp-controls > button:focus,
    .jp-controls > button:hover {
        border-color: #484848;
    }
    /* Repeat */
    .jp-state-looped .jp-repeat,
    .jp-state-looped .jp-repeat:focus,
    .jp-repeat,
    .jp-repeat:focus {
        background: url(https://image.flaticon.com/icons/svg/560/560477.svg) center / 70% no-repeat;
    }
    .jp-repeat,
    .jp-repeat:focus {
        opacity: 0.3;
    }
    .jp-repeat:hover,
    .jp-state-looped .jp-repeat:hover,
    .jp-state-looped .jp-repeat:focus:hover {
        opacity: 0.5;
    }
    .jp-state-looped .jp-repeat,
    .jp-state-looped .jp-repeat:focus {
        opacity: 1;
    }
    /* Volume */
    .jp-volume-controls > button:not(.jp-unmute) {
        opacity: 0.3;
    }
    .jp-volume-controls > button:not(.jp-volume-max) {
        background: url(https://image.flaticon.com/icons/svg/727/727240.svg) center no-repeat;
    }
    .jp-volume-max,
    .jp-volume-max:focus {
        background: url(https://image.flaticon.com/icons/svg/727/727269.svg) center no-repeat;
    }
    .jp-volume-controls > button:hover {
        opacity: 0.5;
    }
    .jp-unmute {
        width: 18px;
        height: 15px;
    }
}
