css:
@-moz-document url-prefix("https://dynalist.io") {
/* Made by SCCW, edited base on Dynalist-Light, thanks to Dynalist-Advanced and Small-font. */
/* Fonts */
 .Node.is-currentRoot > .Node-self .Node-renderedContent, .Node.is-currentRoot > .Node-self .Node-content .pane-item-header, .DocumentBreadcrumb-item, .Node-children .Node-renderedContent, .Node-children .Node-content {
    font-family:Segoe UI, "微软雅黑";
    letter-spacing: 0.7px;
    color: #4b4b4b;
}
.Node.is-currentRoot > .Node-self .Node-renderedContent, .Node.is-currentRoot > .Node-self .Node-content {
    font-weight: bolder;
}
.pane-item-header, .DocumentBreadcrumb-item, .Node-children .Node-renderedContent, .Node-children .Node-content {
    font-size: 14px;
}
/* Item */
 .Node-children .Node-outer .Node {

    border: none;
}
/* Item hover & numbered adjustment */
/* .Node-content:hover, .Node-renderedContent:hover, .Node-note:hover, .Node-renderedNote:hover {
	background: rgba(0, 0, 0, .06);
}*/
.Node-self.is-numbered > .Node-bullet {
	text-align: right;
	width: auto;
	min-width: 22px;
}
/* Notes */
 .Node-note, .Node-renderedNote {
    color: #aaa;
    font-size: 12px;
}
/* Links */
 .DocumentBreadcrumb-item, .node-link {
    color: #aaa;	
}
.DocumentBreadcrumb-item:hover, .node-link:hover {
    /* 	color: rgba(95, 158, 234, 1); */
    color: #70a9ec;
}
.DocumentBreadcrumb-item:active, .node-link:active {
    /* 	color: #4b7cb8; */
    color: #3c89e5;
}
/* Tags */
 .node-tag[title*="@"] {
    color: white !important;
    background-color:#aad !important;
    border-radius: 5px;
    text-decoration: none;
    padding: 3px 5px 3px 5px;
    font-size: 0.9em;
}
* > .node-tag[title*="@"]:hover {
	box-shadow: inset 0px 0px 20px 20px rgba(255, 255, 255, 0.5);
}
.node-tag[title*="#"] {
    color: black !important;
    background-color:#dde !important;
    border-radius: 5px;
    text-decoration: none;
    padding: 3px 5px 3px 5px;
    font-size: 0.9em;
}
* > .node-tag[title*="#"]:hover {
    box-shadow: inset 0px 0px 20px 20px rgba(255, 255, 255, 0.5);
}
.search-match {
    background-color: rgba(255, 255, 0, 0.8) !important;
}
.search-match > .node-tag {
    background-color: rgba(255, 255, 0, 1.0) !important;
    color: black !important;
}
.search-match > .node-tag:hover {
    box-shadow:none;
}
}
