css:
@-moz-document domain("notabenoid.org") {
body , body > header > div{
    background-color: #222!important;
    color:#fff;
}
#f-about .hero, #f-search-top, .announce .b .msg, .sr .tools{
    border: 1px solid #ccc;
    border-radius: 20px;
    background: #333;
    color:white;
}
#tb-main.fixed{
        background: #333;
    color:white;
}
#header-menu li > a{
        color:#fff;
}
a {
    color: #75b3d2;
}
a.act{
    color: #C0C0DC;
}
.shl{
    background: #B79B00;
}
.translator.has-best td.t .best .text{
    font-size: 115%;
}
.close{
    color: #eee;
    opacity: .8;
}
.close:hover{
    color: #fff;
    opacity: 1;
}
.span4.t:hover{
    background-color: #444;
}
select{
    background-color: #444;
    color: #fff;
}
#header-submenu li:hover{
    background-color:#b9b2b230;
}
#Chapters td, #Chapters th, .translator tr.comments-row td, .modal, .modal-footer{
        background-color: #222!important;
}
.translator td.t, .form-actions{
    background:none;
}
.comments .comment:hover .meta, .comments .comment:hover .meta a, .translator tr:hover .info, .translator tr:hover .info a {
    color: #fff;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    color:white;
    background-color: #444;
}
.comments .comment.new {
    background: #234454;
}
.table-striped tbody tr:nth-child(odd) td, .table-striped tbody tr:nth-child(odd) th {
    background-color: #1f272d73;
}
.t:hover, ul.search-results li:hover, .nav > li > a:hover, #header-menu li > a:hover
{
    background-color:#333;
}
#dict-body-content .t{
    background: transparent;
}
.progress .text{
    color: black;
}
.nav-tabs > .active > a, .nav-tabs > .active > a:hover {
    color: #9d9d9d;
    background-color: #222222;
}
.chic-pages .blur.right, .chic-pages .blur.left{
    display:none!important;
}
ul.selectable a:hover {
    background: #464646;
    border-radius: 2px;
    box-shadow: 0 1px 2px #616161 inset;
}
.icon-briefcase {
    filter:invert(1);
    }
.pagination a{
	background-color:#363636;
}
.pagination a:hover{
	background-color:#222222;
}
.pagination .active a:hover{
	background-color:#949494;
}
.button.btn, input[type="submit"].btn, button[onclick="restore()"], .btn.btn-small, input[type="submit"].btn.btn-small, input[class="btn btn-mini mytalks"], input[class="btn btn-mini cancel"], button[class="btn btn-mini stop"]{
color: #ffffff;
    text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.25);
    background-color: #363636;
    background-image: -moz-linear-gradient(top, #444444, #222222);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#444444), to(#222222));
    background-image: -webkit-linear-gradient(top, #444444, #222222);
    background-image: -o-linear-gradient(top, #444444, #222222);
    background-image: linear-gradient(to bottom, #444444, #222222);
    background-repeat: repeat-x;
/*    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#ff444444', endColorstr='#ff222222', GradientType=0);*/
    border-color: #222222 #222222 #000000;
    border-color: rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.1) rgba(0, 0, 0, 0.25);
}
.button.btn, input[type="submit"].btn:hover, button[onclick="restore()"]:hover, .btn.btn-small, input[type="submit"].btn.btn-small:hover, input[class="btn btn-mini mytalks"]:hover, input[class="btn btn-mini cancel"]:hover, button[class="btn btn-mini stop"]:hover{
color: #333333;
    color: #ffffff;
    background-color: #222222;
}
.chic-pages a{
    color: #bbb;
}
.chic-pages a:visited{
    color: #fff;
}
.icon-star, .icon-plus, .caret, .icon-wrench, .icon-random, .icon-refresh, .icon-book, .icon-cog, .icon-pencil, .icon-upload, .icon-ok, .icon-ban-circle, .icon-list, .icon-bullhorn, .icon-filter, .icon-thumbs-down, .icon-comment, .icon-remove-sign, .icon-remove{
	filter:invert(1);
}
#upic-modal p.i{
    opacity: 1;
}
.i.i{
        opacity: .7;
}
[class^="icon-"], [class*=" icon-"]{
    filter:invert(1);
    opacity: 1;
}
.icon-white{
    	filter:invert(0);
}
.help-block{
    color:lightgrey;
}
.user.user-inactive{
    color:lightgrey!important;
}
.sr dl.info dt{
    color:lightgrey!important;
}
.sr dl.info .more_btn{
    color:lightgrey!important;
}
.post .info{
    background-color:#333;
}
.open > .dropdown-menu{
    background-color:#333;
}
.dropdown-menu a, #dict-tools a{
    color:#fff;
}
.btn-group.open .btn.dropdown-toggle {
    background-color: #333;
}
blockquote{
    border-left-color: #555;
}
.translator .tools, .translator .tools a.xp, #tb-filter, #dict-add{
    background-color: #333;
}
.translator td.b{
    background-color: #333;
}
.translator td.c, .translator td.u{
    background-color: #444;
}
.translator tr.m td.c{
    background-color: #A48B00;
}
#Lenta .orig .meta {
    background: #333;
}
.translator .rating.x .current, .translator .rating .votes .pos, .translator .rating .pane.neg{
    background-color: #333;
}
.translator tr .rating:hover .current{
    background-color: #333;
}
.translator .rating .votes .total{
    background-color: #555;
    color: #fff;
}
.translator tr td .rating .vote, .translator .rating .current{
    background-color: #333;
    color:#fff;
    box-shadow: 0 1px 2px #555 inset;
}
#header-logo{
	filter: hue-rotate(180deg) invert(1);
}
.translator tr.m td, .translator tr.m .tools{
    background-color: #ccad00;
}
.translator tr.m .tools, .translator tr.m .tools a.xp, .translator tr.m td.b, .translator tr.m .rating .current{
    background-color: #B79B00;
}
.translator tr.m .tools a.xp, .translator tr.m .tools a.xp i{
    background-color: transparent;
}
.translator tr.m:hover td .info a{
    color:#eee;
}
.translator tr.m td .info a{
    color:#ddd;
}
.translator tr.m td .info a:hover{
    color:#fff;
}
.translator tr.m td .info{
	color:#ddd;
}
.translator tr.m:hover td .info{
	color:#fff;
}
.translator thead th{
    background-color:#888;
    border-bottom-color: #bbb;
}
#Notices li.new, .verse:hover{
    background:#666!important;
}
.verse .rate{
 background: #555;   
}
form[method="post"]{
    background-color: #444;
}
.translator tbody td{
    border-bottom: 2px solid #999;
}
.translator td.t > div{
    border-bottom: 1px solid #555;
}
}
