css:
@-moz-document url-prefix("https://www.php.net/") {
/* initial */

body
{
/*     position: fixed; */
    max-width: 100vw;
    height: 100%;
    overflow-y: auto;
    overflow-x: hidden;
}

#layout-content {
	position: relative;
    float: left;
    width: 95%;
    overflow: auto;
    top: 0;
    left: 0;
    bottom: 0;
    overflow: auto;
}

.layout-menu {
	position: fixed;
    float: none;
    z-index: 1;
    right: -28%;
    top: 15%;
    bottom: 0;
    padding: .75rem 0.5rem 1.5rem;
    width: 33%;
    transition: right 0.5s, padding 0.5s;
    overflow: auto;
}
#layout
{
    width: 100%;
}

#layout,
.layout-menu,
body
{
    background: inherit;
}


.layout-menu:hover {
    right: 0;
    padding: .75rem 1.5rem 1.5rem;
    transition: right 0.5s, padding 0.5s;
}

@media print
 {
 
	body
     {
    	margin: 4.23mm;
        font-size: 12px;
        font-family: sans-serif;
        line-height: 1.2em;
        color: #212121;
     }
    
    h1::before
     {
     	float: left;
        margin-right: 1em;
     	content: "h1:";
        color: #9E9E9E;
     }
    h1
     {
/*      	border-top: 6px solid #9E9E9E; */
     	padding-top: 1em;
