        border-radius: 5px;
        color: #263238
     }
    h6::before
     {
     	float: left;
        margin-right: 1em;
     	content: "h6:";
        color: #9E9E9E;
     }
    h6
     {
/*      	border-top: 1px solid #9E9E9E; */
     	padding-top: 1em;
        margin-top: 1em;
        box-shadow: 0 -1px 2px -2px #9E9E9E;
        border-radius: 5px;
        color: #263238
     }
     
     
    :link,
    :visited,
    :active
     {
     	color: inherit;
     }
     
     
	#head-nav,
    #trick,
    #goto,
    #breadcrumbs,
    .page-tools,
    .container.footer-content,
    .layout-menu,
    .genanchor
     {
     	display: none;
     }
     
     
    .phpcode
     {
     	padding: 0.25em;
        border-radius: 0.25em;
/*      	background-color: #f0f0f0; */
        border: thin solid #E0E0E0;
        font-size: 0.8em;
        font-weight: 400;
        line-height: 1.2em;
     }
     
     
    .classname,
    .methodname,
    .function,
    .type
     {
     	font-family: monospace;
     	color: #646464;
     }
     
     
    .cdata
     {
        font-size: 0.8em;
     	color: #646464;
        padding: 0 1em;
     }
     
     
/*     div.phpcode code span[color="#0000BB"] { color: #1976D2 !important; } */
/*     [color=attr(color color)]{ color: #1976D2 !important; } */
    [style="color: #0000BB;"]{color: #311B92 !important;} /* indigo */
    [style="color: #0000BB"] {color: #0D47A1 !important;} /* blue */
    [style="color: #007700"] {color: #1B5E20 !important;} /* green */
    [style="color: #FF8000"] {color: #F57F17 !important;} /* yellow */
    [style="color: #DD0000"] {color: #D50000 !important;} /* red */
}
}
