css:
@-moz-document url-prefix("https://tvcultura.com.br") {
/* Corpo */
body {
    background: #2a2a2e;
    color: #fff;
}
a {
    color: #fff;
    transition: all 0.5s;
}
a:hover {
    color: #213f98;
}
/* Header */
main > header > div.top-menu {
    background: #3a8e42;
}
#fpa-menu {
    background: #35843c;
}
main > header > .menu-principal-tv > h1 {
    filter: brightness(8.0);
}
main > header {
    background: #4cb856;
    border: 1px solid #4a4a4f;
}
main > header > .menu-principal-tv > nav.menu > a {
    color: #fff;
    margin: 7px 20px 0px 20px;
    transition: all 0.5s;
}
main > header > .menu-principal-tv > form {
    background: #4cb856;
}
/* Busca */
main > header > .menu-principal-tv > form > input[type="search"] {
    background: #3a8e42;
    border: 1px solid #3a8e42;
    border-radius: 50px;
    color: #fff;
    transition: all 0.5s;
}
main > header > .menu-principal-tv > form > input[type="search"]:hover {
    background: #fff;
    color: #000;
}
main > header > .menu-principal-tv > form > button[type="submit"] {
    filter: brightness(8.0);
}
/* Destaques */
.destaques {
    border-bottom: 1px solid #4a4a4f;
    background: #38383d;
}
/* Programação Home */
main > section.content > div > nav.agora > h3 {
    background: #38383d;
    color: #fff;
}
main > section.content > div > nav.agora > ul > li > a {
    color: #fff;
}
main > section.content > div > nav.agora > ul > li {
    border-bottom: 1px solid #4a4a4f;
}
nav.redes {
    border: 1px solid #4a4a4f;
    background: #38383d;
}
nav.redes > h3 {
    color: #fff;
}
/* Titulos */
main > section.content > div > nav.agora h2 {
    color: #fff;
    border-bottom: 5px solid #4a4a4f;
}
main > section.content > div > h2,
section.content > section > h2 {
    color: #fff;
    border-bottom: 5px solid #4a4a4f;
}
section.content > h1 {
    background: #2a2a2e;
    color: #fff;
}
/* Home Box */
#home > main > section.content > div:nth-child(1) > article > section:nth-child(2) > section {
    border-bottom: 1px solid #4a4a4f;
}
#home .ultimas > section > a {
    transition: all 0.5s;
}
#home .ultimas > section > a:hover {
    filter: brightness(0.6);
}
/* Seção Programas */
main > section.content > section > article > div > h3 a {
    color: #ffff;
}
main > section.content > section > article > div > p a {
    color: #ffff;
}
/* Acontece Titulos */
#programas-box-info {
    border-bottom: 1px solid #4a4a4f;
    background: #38383d;
}
#acontece #programas-box-info > aside {
    border-top: 1px solid #4a4a4f;
}
#programas-box-info > aside {
    border-bottom: 1px solid #4a4a4f;
    background: #38383d;
}
#programas-box-info > aside > h2 {
    color: #fff;
}
#programas-box-info > aside > p {
    color: #fff;
}
#acontece .conteudo-asset,
#acontece .conteudo-asset p,
#acontece .conteudo-asset article div {
    color: #fff;
}
.pagination > ul > li a.active,
.pagination > ul > li a {
    background-color: #2a2a2e;
    transition: all 0.5s;
}
.pagination > ul > li a.active,
.pagination > ul > li a:hover {
    background-color: #4cb856;
}
/* Grade */
#carrossel-grade ul > li {
    border-right: 2px solid #4a4a4f
}
.wrapper-carrossel > a.prev {
    background: url(https://tvcultura.com.br/_img/tvcultura/icones/seta-diteita-grade@2x.png) no-repeat center #2a2a2e;
}
.wrapper-carrossel > a.next {
    background: url(https://tvcultura.com.br/_img/tvcultura/icones/seta-esquerda-grade@2x.png) no-repeat center #2a2a2e;
}
.grade-box {
    background: #38383d;
    border: 1px solid #4a4a4f;
}
.grade-box > section {
    background: #38383d;
}
.grade-box > div.info-programa {
    background: #38383d;
}
.grade-box h2 > a {
    color: #fff;
}
.grade-box > section.mais h2 {
    color: #fff;
}
.grade-box > section.mais div {
    color: #fff;
}
.grade-box > section.mais a {
    color: #fff;
    border: 1px solid #4cb856;
    background: #4cb856;
    border-radius: 50px;
}
.grade-box > section.mais p {
    color: #fff;
}
/* Programas */
main > section.content > section.programas > article {
    border-bottom: 1px solid #4a4a4f;
}
/* Programas Page */
#sobre > section > p {
    color: #fff;
}
#programas.interna > main > hr {
    background: #4a4a4f;
}
#programas > main > section > aside > h2 {
    color: #fff;
}
#programas > main > section > aside > ul {
    background: #38383d;
    border: 1px solid #4a4a4f;
}
#programas > main > section > aside > ul > li > div > a > p {
    color: #fff;
}
#programas > main > section > aside > ul li div a {
    border-bottom: 1px solid #4a4a4f;
}
a.share {
    color: #fff;
    background: #213f98;
    border: 1px solid #213f98;
    border-radius: 50px;
    transition: all 0.5s;
}
a.share:hover {
    color: #fff;
    background: #162a66;
    border: 1px solid #213f98;
}
#programas.video > main > section > section.programas > article > div > h2 > a {
    color: #fff;
}
#programas-box-info img {
    float: right;
}
#sobre > section > a {
    border-radius: 50px;
}
#programas > main > section > section > article {
    margin-top: 60px;
}
/* Buscas */
#busca > main > section > section.list > div > article {
    border-bottom: 1px solid #4a4a4f;
}
#busca > main > section > section.list > div > article > a > h1 {
    color: #fff;
}
#busca > main > section > section.list > div > article > a > div:nth-child(3) {
    color: #fff;
}
#busca > main > section > section.result-programas > article {
    background: #38383d;
    border: 1px solid #4a4a4f;
}
#busca > main > section > div.carregar-mais > a {
    border-radius: 50px;
    background: #4cb856;
    color: #fff;
    border: 0px;
    margin-top: 20px;
    padding: 15px;
    width: 50%;
    transition: all 0.5s;
}
#busca > main > section > div.carregar-mais > a:hover {
    background: #35843c;
}
/* Rodape */
main > footer {
    background: #38383d;
    border-top: 1px solid #4a4a4f;
}
main > footer a {
    color: #fff;
}
main > footer small {
    background: #3a8e42;
}
}
