/* ==UserStyle==
@name           UNLaM - Intraconsulta
@namespace      https://github.com/mngomezz/UNLaM-Modo-Oscuro
@version        5.11.1
@description    Modo oscuro para miel.unlam.edu.ar
@author         Martin Gomez
@homepageURL    https://github.com/mngomezz/UNLaM-Modo-Oscuro/blob/master/Dark_Intraconsulta.user.css
==/UserStyle== */
@-moz-document domain("alumno2.unlam.edu.ar") {
        body, .container {
            background-color: #272727 !important;
            color: white !important;
        }
        .navbar-default {
            background-image: linear-gradient(to bottom,#404040 0,#404040 100%) !important;
            border: none !important;
            color: white !important;
        }
        .navbar-brand img {
            content:url("https://cdn.unlam.edu.ar/static/imagenes/logo_unlam_34_blanco.png");
            margin-top:7px;
        }
        .jumbotron {
            background-color: #404040 !important;
            color: white !important;
        }
        .navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.active>a {
            background-image: linear-gradient(to bottom,#363636 0,#363636 100%) !important;
            color: white !important;
        }
        .navbar-default .navbar-nav>li>a, footer a, li a, .text-info, .pull-left a, a b, .btn-warning, .btn-primary, .btn-success {
            color: white !important;
        }
        .navbar .container, #bs-example-navbar-collapse-9, .dropdown-menu, .nav .open>a, .nav .open>a:hover, .nav .open>a:focus, .nav>li>a:hover, .nav>li>a:focus, .table-striped>thead>tr {
            background-color: #404040 !important;
            color: white !important;
        }
        .table>tbody>tr {
            background-color: #404040 !important;
        }
        font {
            color: #272727 !important;
        }
        a {
            color: #61abea !important;
        }
        .form-control {
            background-color: #404040 !important;
            color: white !important; 
            border: none !important;
        }
        input:-webkit-autofill {
            -webkit-box-shadow: 0 0 0 30px #404040 inset !important;
            -webkit-text-fill-color: white;
        }
        small {
            color: lightgrey !important;
            line-height: 20px !important;
        }
        a.btn{
            white-space:inherit !important;
            text-align:inherit !important;
        }
        .alert-success {
            background-image: linear-gradient(to bottom,#4c974d 0,#3c763d 100%) !important;
            color: #c8e5bc !important;
            border: none !important;
        }
        p a, .text-success {
            color: #98cc63 !important;
        }
        .alert-success hr {
            border-top-color: #c8e5bc !important;
        }
        .table>thead>tr>td.active, .table>tbody>tr>td.active, .table>tfoot>tr>td.active, .table>thead>tr>th.active, .table>tbody>tr>th.active,           .table>tfoot>tr>th.active, .table>thead>tr.active>td, .table>tbody>tr.active>td, .table>tfoot>tr.active>td, .table>thead>tr.active>th,           .table>tbody>tr.active>th, .table>tfoot>tr.active>th, .table-striped>tbody>tr:nth-child(even) {
            background-color: #222222 !important;
            color: white !important;
        }
        .table-striped>tbody>tr:nth-child(odd) {
            background-color: #303030 !important;
            color: white !important;
        }
        #link00:hover, #link00:focus {
            background-image: linear-gradient(to bottom,#428bca 0,#428bca 100%) !important;
        }
        .dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
            background-image: linear-gradient(to bottom,#595959 0,#595959 100%) !important;
        }
        .btn-default {
            background-image: linear-gradient(to bottom,#595959 0,#595959 100%) !important;
            border: none !important;
            margin-bottom: 10px;
            background-color: #595959 !important;
            color: white !important;
        }
        .btn-default:hover, .btn-default:focus {
            background-color: #595959;
            background-position: 0 -15px;
        }
        td a {
            color: #9cc7f6 !important;
        }
        .alert-warning, .alert-success {
            display: inline-block !important;
        }
        .btn-primary:hover {
            background-color: #2d6ca2 !important;
            background-position: 0 -15px !important;
        }
        .form-group {
            margin-top: 5px !important;
        }
        .table>thead>tr>td.danger, .table>tbody>tr>td.danger, .table>tfoot>tr>td.danger, .table>thead>tr>th.danger, .table>tbody>tr>th.danger,           .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>tbody>tr.danger>td, .table>tfoot>tr.danger>td, .table>thead>tr.danger>th,           .table>tbody>tr.danger>th, .table>tfoot>tr.danger>th {
            background-color: black !important;
        }
        .text-warning {
            color: #f94a4a !important;
        }
        input {
            background-color: #404040 !important;
        }
        .bg-info {
            background-color: #272727 !important;
        }
        tr {
            background-color: #404040 !important;
            color: white !important;
        }
}
