css:
@-moz-document domain("blogtruyen.vn") {
#menu, #mota, #breadcrumbs, .widget-top-forum, .main-content .sidebar, footer, .tab-comments, .relative-storys, .social-button, .manga-detail center, .manga-detail hr, .last-item .view-all, .story-note, #banner, .qc-inner, .fb-comments,.qc_TC_Chap_Middle {
    display: none
}
.main-content .list-mainpage, .view-type, .manga-detail {
    width: 980px
}
.fl-r, .detail .content {
    width: 760px !important;
    color: #b1b1b1
}
.fl-r p {
    padding: 0 20px
}

body, .bg-white, #storyPinked, .manga-detail, .thumbnail, .manga-detail .votes{
    background: #181818;
    border: none
}
#toolbar, #toolbar ul.menu ul.top-submenu {
    background: rgba(0, 0, 0, 0.7)
}
.fl-r {
    width: 700px !important
}
.detail .content {
    width: 960px
}
.description p, .c-content .user-info a, .list-mainpage.gridview .storyitem h3 a {
    color: #0aa
}
#btnBookmart {
    background: #181818;
    border: 1px solid #0cc;
    color: #ccc
}
.c-content .user-info, #form-comment h3, #loadChapter span, #mceu_17 .mce-ico {
    color: #ddd
}
.c-content .commment-content, .sub-content .commment-content p, .list-mainpage.gridview .storyitem .statictis {
    color: #aaa
}
.list-comment .ul-comment .sub-comment .sub-content, .manga-detail .list-chapters .list-wrap p:nth-child(2n+1) {
    background: #181818
}

#readonline .comments {
    width: 900px !important
}
.list-comment .comment-scroll {
    max-height: 830px
}
#readonline section, .gridview,#mceu_17 .mce-btn, .mce-panel,.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th{
    background-color: #181818 !important
}
#readonline h1 {
    color: #0cc;
    text-shadow: none
}
#toolbar .frm-q-search input[type='text'] {
    background: transparent;
    border: 1px solid #ccc
}
#toolbar .frm-q-search input[type='submit'] {
    background: transparent;
    color: #ff0;
    border: 1px solid #00f
}
.manga-detail .description .category {
    background-color: #303030
}
.list-manga-bycate .list p,.list-manga-bycate .list p:hover,.list-manga-bycate .list p:nth-child(1):hover{background-color:#181818;color:#0cf}
.list-manga-bycate .list p>span:nth-child(n) a,.list-manga-bycate .orderby,.ListStoryPage h1.bold{color:#0cf}
/*.list-manga-bycate .list p>span:nth-child(1) a

/*login page*/
.contain-sm .text-light {
    color: #0cc
}
section.section-account .img-backdrop {
    background-image: none !important
}
section.section-account .spacer {
    height: 0px
}
}
