css:
@-moz-document domain("nooksguide.com") {
body {
    color: #eeeeee;
}
.bg {
    background-color: #393e46;
    background-image: none;
}
.bg:nth-child(even) {
    background-color: #41464f;
}
.bg > .container {
    z-index: 1;
}
.bg::after {
    display: none;
}
.bg-footer a {
    color: #eeeeee;
}
.bg-footer::before {
    background: #1A202C;
    opacity: 1;
    z-index: -1;
}
.menu {
    background-color: #31363d;
    background-image: none;
    color: #eeeeee;
}
.navbar-light .navbar-toggler-icon {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' width='30' height='30' viewBox='0 0 30 30'%3e%3cpath stroke='rgba(238, 238, 238, 1)' stroke-linecap='round' stroke-miterlimit='10' stroke-width='2' d='M4 7h22M4 15h22M4 23h22'/%3e%3c/svg%3e");
}
.btn {
    z-index: 1;
}
.box {
    background: #31363d;
}
.box-header {
    color: #eeeeee;
}
.box-header.box-header-blue h1 {
    color: #eeeeee;
    position: relative;
}
.table-striped {
    background-color: #393e46;
    color: #E2E8F0;
}
.table-striped tbody tr:nth-of-type(2n+1) {
    background-color: #41464f;
}
.pro-design-list {
    background-color: #393e46;
    -webkit-box-shadow: 0 8px 0 0 #22252a;
    -moz-box-shadow: 0 8px 0 0 #22252a;
    box-shadow: 0 8px 0 0 #22252a;
}
.pro-design-list:hover {
    -webkit-box-shadow: 0 8px 0 0 #22252a;
    -moz-box-shadow: 0 8px 0 0 #22252a;
    box-shadow: 0 8px 0 0 #22252a;
}
.news h3 small {
    color: #CBD5E0 !important;
}
}
