css:
@-moz-document domain("n-tv.de") {
.home,
.body {
    background: #253960 url("data:image/png;base64,") top left repeat-x;
    color: #fff;
    margin: 0 auto;
    float: none;
}

/* Haupt-Div mit Texten*/
.container {
    background: #253960 !important;
    font-size: x-small;
}

/* Headlines Startseite */
.teaser__headline {
    color: #7e90bd;
}
A:visited > .teaser__headline {
    color: #35497a;
}
A:hover > .headline {
    color: #A5B9Fa;
    text-shadow: 3px 2px 3px #fff;
}
/* Kickers Startseite */
.teaser__kicker {
    color: #bf262b;
}
A:visited > h2 > .teaser__kicker {
    color: #35497a;
}
A:visited > h2 > .teaser__headline {
    color: #35497a;
}
/* Info Boxen rechts Startseite */
.sidebar-teasers {
    background: #e1e1f0;
}
/*---------------------------- */
/* Seiten mit Artikeln */
.no-js > body {
    margin: 0 auto;
    background: #253960;
    color: #d9d9d9;
}
.sitewrapper {
    background: #253960;
}
.article__headline,
.day__teaser__headline {
    color: #7e90bd;
}
.article__kicker {
    color: #7e90bd;
}

.article__aside__title {
    color: #7e90bd;
    border: 1px dotted red;
}
.article__aside--text, figcaption {
    color: #d9d9d9;
}
.title--dark {
    color: #a13f42;
    text-shadow: 3px 2px 3px #fff;
}

/*Chartboxen in den Artikeln - IFRAME*/
div[class*="article__aside"]{
       background: #3c4e72;    
    margin: 0px;
}
    


/*--------------------------------*/
/* Menü im Kopfbereich */
.header__nav__element {
    background: #253960;
    color: #7e90bd;
}
.header__nav__submenus > .row {
    background: #d0d0d6;
    color: #7e90bd;
}
}

@-moz-document domain("datawrapper.dwcdn.net") {
/*-------------------------------------*/
/* Elemente innerhaup der Seiten (Diagramme, Infoboxen) */
/* Charts: datawrapper.dwcdn.net */
.dw-chart, .svelte-lgwtoo {
    color: #d9d9d9;
}
.text-label {
    color: #d9d9d9 !important;
}

.header, .chart, div[class*="label"] > span {
    color: #d9d9d9 !important;    
}

.chart {
    background: #3c4e72;    
    margin: 0px;
}
}
