css:
@-moz-document url-prefix("https://diolinux.com.br") {
/* Corpo */
body {
    background: #2a2a2e;
    color: #fff;
}
a {
    color: #0094ff;
}
h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
    color: #fff;
}
h1 a,
h2 a,
h3 a,
h4 a,
h5 a,
h6 a {
    color: #fff;
}
h1 a:hover,
h1 a:focus,
h2 a:hover,
h2 a:focus,
h3 a:hover,
h3 a:focus,
h4 a:hover,
h4 a:focus,
h5 a:hover,
h5 a:focus,
h6 a:hover,
h6 a:focus,
.h1 a:hover,
.h1 a:focus,
.h2 a:hover,
.h2 a:focus,
.h3 a:hover,
.h3 a:focus,
.h4 a:hover,
.h4 a:focus,
.h5 a:hover,
.h5 a:focus,
.h6 a:hover,
.h6 a:focus {
    color: #c4c4c4
}
input[type="text"],
input[type="email"],
input[type="url"],
input[type="password"],
input[type="search"],
input[type="number"],
input[type="tel"],
input[type="range"],
input[type="date"],
input[type="month"],
input[type="week"],
input[type="time"],
input[type="datetime"],
input[type="datetime-local"],
input[type="color"],
select,
textarea {
    background: #2a2a2e;
    color: #fff;
    border: 1px #4a4a4f solid;
}
input[type="text"]:focus,
input[type="email"]:focus,
input[type="url"]:focus,
input[type="password"]:focus,
input[type="search"]:focus,
input[type="number"]:focus,
input[type="tel"]:focus,
input[type="range"]:focus,
input[type="date"]:focus,
input[type="month"]:focus,
input[type="week"]:focus,
input[type="time"]:focus,
input[type="datetime"]:focus,
input[type="datetime-local"]:focus,
input[type="color"]:focus,
select:focus,
textarea:focus {
    background: #111113;
}
/* Busca */
.page-header {
    background: #38383d;
}
.archive-count {
    color: #fff;
}
.searchwp-live-search-results.searchwp-live-search-results-showing {
    background: #2a2a2e;
}
.searchwp-live-search-results.searchwp-live-search-results-showing .searchwp-live-search-result:not(:last-child) a {
    border-bottom: 1px #4a4a4f solid;
}
.searchwp-live-search-results.searchwp-live-search-results-showing .searchwp-live-search-result a:hover,
.searchwp-live-search-results.searchwp-live-search-results-showing .searchwp-live-search-result a:focus {
    background: #424246;
}
/* Menu */
.site-search-wrap {
    background: #38383d;
}
.site-search input[type="search"] {
    background: #2a2a2e;
    color: #fff;
    border: 1px #4a4a4f solid;
}
.offcanvas-sidebar {
    background: #38383d;
}
.pk-font-heading {
    --pk-heading-font-color: #fff;
}
.widget_nav_menu .menu > .menu-item:not(:first-child) {
    border-top: 1px #4a4a4f solid;
}
/*Cartões */
.archive-wrap .archive-borders-enabled.archive-list article,
.archive-wrap .archive-borders-enabled.archive-grid article,
.archive-wrap .archive-borders-enabled.archive-masonry article {
    border: 1px solid #4a4a4f;
    background: #38383d;
}
.entry-excerpt {
    color: #fff;
}
.note,
.post-meta {
    color: #fff !important;
}
.post-meta a,
.post-meta .author a {
    color: #0094ff;
}
.archive-wrap {
    background: #2a2a2e;
}
/* Artigos */
.pk-social-links-wrap {
    --pk-social-link-color: #fff;
}
.pk-share-buttons-wrap {
    --pk-share-link-background: #38383d;
    --pk-share-link-color: #fff;
}
.post-tags a {
    background: #38383d;
    color: #fff;
}
.title-block,
.pk-font-block,
.pk-widget-contributors .pk-author-posts > h6,
.cnvs-block-section-heading {
    color: #fff;
}
.comment-body + .comment-respond,
.comment-list + .comment-respond {
    border-top: 1px #4a4a4f solid;
}
.cs-nextpost-section {
    border-top: 1px #4a4a4f solid;
}
/* Anuncio Rodape */
.ezmob-footer-desktop {
    background-color: rgba(74, 74, 79, 0.8);
    border-top: 1px solid #737373;
}
.ezmob-footer-close {
    color: #ffff;
    border: 1px solid #4a4a4f;
    background: #38383d;
}
/* Rodape */
.navbar-nav > li > a {
    color: #fff;
}
}
