css:
@-moz-document domain("everymac.com") {
div[class^="prompt"],
#contentright,
#topitemwrapper,
#navigationleft,
#hostsponsorbox,
#toprightcorner,
.disclaimer,
#integratedbanner,
#contentleft,
#lookupembedbanner {
    display:none;
    position:absolute;
    top:0px;
}

body {
    background:#e9eff3
}

#wrapper {
    position:relative;
    z-index:100;
}

#contentcenter {
    width:auto;
    padding-top:0px;
}

#navigationcenter {
    width: 100%;
    color:hsla(204, 5%, 32%, 1);
    background-color: hsla(204, 5%, 32%, 1);
    background-image: none;
    height: 47px;
}

#navmodules {
    overflow:hidden;
}

#navigationcenter a {
    text-decoration: none;
    font-weight: 300 !important;
    padding: 18px 11px;
    position: relative;
    top: 1px;
    margin-right: -13px !important;
    background: #ffffff2b;
}

#navigationcenter a:hover {
    background:#00000036;
}

#lookup-form {
    background: hsla(216, 8%, 76%, 0.68);
    border: 1px solid #e9eff3;
    margin-left: -17px;
    margin-right: -17px;
    margin-bottom: 10px;
    padding: 0px 20px;
}

#contentcenter_specs_table table {
    width:100%;
}

#contentcenter_specs_table table td {
    border-right: 1px solid hsla(0, 0%, 89%, 1);
    border-bottom: 0px solid white;
    border-top: 0px solid white;
    vertical-align:top;
}

#contentcenter_specs_table div:nth-child(even) {
    background:hsl(198, 10%, 95%);
    margin-left:-5px;
    padding-left:5px;
    padding-right:10px;
    padding-bottom:0px;
    border-right:0px;
}

#contentcenter_specs_table div:nth-child(even) td {
    padding:15px 5px;
    color:#3e3e3e;
    font-family:"Consolas";
}
#contentcenter_specs_table div:nth-child(even) td {
    border:0px !important;
}

#contentcenter_specs_table div:nth-child(odd) td {
    vertical-align:middle;
}

.mocklink {
    color: #000;
    font-size: 12px !important;
    background: hsla(0, 0%, 99%, 1);
    font-weight: bold;
}
}
