css:
@-moz-document url-prefix("https://roblox.com/"), url-prefix("https://web.roblox.com/"), url-prefix("https://www.roblox.com/") {

.thumbnail-2d-container[thumbnail-type=avatarHeadshot] {
    border: 0;
    padding: 0;
    border-radius: 0;
    vertical-align: bottom;
    background-color: #f5deb300;
}

.rbx-tabs-vertical .tab-content-group {
    display: block;
    position: relative;
    margin-top: 35%;
}

.game-card-thumb-container:hover {

    transform: scale(1.05);
    transition: 0.3s;
}


.dark-theme:before .game-card-container:before .game-card-thumb-container:before {
    background-color: rgba(57, 59, 61, 0);
    box-shadow: 0 5px 10px 0 rgb(11, 12, 12);
    border-radius: 20px;
    width: 200px!important;
}




.game-card-thumb-container:before {
    border-radius: 8px;
    position: absolute;
    content: "";
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background: radial-gradient(transparent,rgba(194, 194, 194, 0));
    transition: opacity .3s linear;
    opacity: 0;
}

.dark-theme .game-card-container .game-card-thumb-container {
    background-color: rgba(57, 59, 61, 0);
    box-shadow: 0 5px 10px 0 rgb(11, 12, 12);
    border-radius: 20px;
}

.game-card-container thumbnail-2d .game-card-thumb, .game-card-thumb-container .game-card-thumb {
    height: 150px;
    width: 150px;
    overflow: hidden;
    display: inline-block;
    margin: auto;
    border-radius: 13px!important;
}



.rbx-header {
background: url(https://i.imgur.com/5ArZ3Sf.png);
    background-repeat: round;
}

.rbx-navbar-search .input-group {
    background-image: url(https://i.imgur.com/NLUGXT4.png);
    background-size: initial;
    background-repeat: round;
}

.dark-theme .rbx-header .rbx-navbar-search .input-field {
    padding-left: 33px;
    border-radius: 12px;
    border: 0px solid #656668;
}

.dark-theme .input-group .input-field {
    background-color: transparent;
    border-color: transparent;
    color: transparent;
}

.dark-theme .rbx-header .rbx-navbar-search .input-group {
    height: 28px;
    border-radius: 10px;
}

.dark-theme .rbx-header .rbx-navbar-search {
    background-color: transparent!important;
}

.dark-theme .rbx-header {
    background-color: #191b1d;
    border: solid #111214;
    border-width: 0 0 0px;
    box-shadow: 0 5px 12px 0 rgb(49, 113, 156);
    border-radius: 10px!important;
    margin: 1%;
}

.dark-theme .rbx-left-col {
    box-shadow: 0 0 0px 0 rgba(0,0,0,.3);
    background-color: transparent;
}

.content {
    max-width: 1338px;
    background-color: #e3e3e3;
    padding-top: 12px;
    margin: 0 auto;
    margin-top: 4%!important;
}

.rbx-left-col .rbx-scrollbar {
    height: 100%;
    height: calc(100% - 90px);
    margin-top: 10%;
}

.rbx-left-col li .text-nav:active, .dark-theme .rbx-left-col li .text-nav:link, .dark-theme .rbx-left-col li .text-nav:visited {
    color: #fff0;
}

.rbx-nav-sponsor img {
    max-width: 100%;
    height: auto;
    vertical-align: middle;
    margin-bottom: 0%!important;
}

#mCSB_1_container ul li a span, #mCSB_1_container ul li a span:hover {
    color: #fff!important;
}


.avatar-card-fullbody .avatar-card-image, .avatar-card-fullbody .avatar-card-link, .avatar .avatar-card-image, .avatar .avatar-card-link {
    border: 0;
    border-radius: 20%;
    box-shadow: 0 5px 10px 0 rgba(71, 151, 177, 0.3);
}

.dark-theme .avatar {
    background-color: #23252700;
    color: #fff0;
}

.avatar-card-fullbody .avatar-card-image, .avatar .avatar-card-image {
    width: 100%;
    height: 100%;
    word-break: break-all;
    vertical-align: top;
    background-color: #d1d1d100;
    background-image: url(https://i.imgur.com/5ArZ3Sf.png);
    background-size: 180%;
}

.icon-default-friendstatus, .icon-game, .icon-online, .icon-studio {
    background-image: url(https://i.imgur.com/30LVbid.png);
    background-repeat: no-repeat;
    background-size: 100% auto;
    width: 9px;
    height: 9px;
    border-radius: 30%;
    display: inline-block;
    vertical-align: middle;
    margin-right: 89px;
    margin-top: 35px;
}

.dark-theme .game-card-container .game-card-name {
    margin-bottom: 4px;
    padding: 0;
    color: transparent;
}

.dark-theme .game-card-container .game-card-name {
    padding: 0;
    color: transparent;
    position: fixed;
}

.dark-theme .game-card-info .info-label.icon-votes-gray, .dark-theme .game-card-info .info-label.icon-votes-gray-white-70, .icon-rating-sm, .light-theme .game-card-info .info-label.icon-votes-gray, .light-theme .game-card-info .info-label.icon-votes-gray-white-70 {
    background-position: 0 -32px;
    margin-top: 20px;
}

.dark-theme .game-card-info .info-label.icon-playing-counts-gray, .dark-theme .game-card-info .info-label.icon-playing-counts-gray-white-70, .icon-currently-playing-sm, .light-theme .game-card-info .info-label.icon-playing-counts-gray, .light-theme .game-card-info .info-label.icon-playing-counts-gray-white-70 {
    background-position: 0 -48px;
    margin-top: 20px;
}

.dark-theme .game-card-info .info-label {
    color: hsla(0,0%,100%,.7);
    margin-top: 20px;
}

.game-cards .game-card .game-card-info, .game-cards .game-card .game-card-name {
    line-height: 1.4em;
    padding-left: 11%;
}


.home-header-content h1 {
    margin: 36px 0 12px!important;
}

.gotham-font .font-title, .gotham-font .h1, .gotham-font .h2, .gotham-font h1, .gotham-font h2 {
    -webkit-text-stroke: 0px;
}





/*Game's page*/

a.btn-common-play-game-lg:hover {
    transition: .5s;
    transform: scale(1.05);
}

a.btn-common-play-game-lg {
    transition: .5s;
    transform: scale(1);
}

.icon-additem, .icon-arrow-right-blue, .icon-audio, .icon-back, .icon-bigplay, .icon-bigplay-fill, .icon-bigstop, .icon-bigstop-fill, .icon-brokenpage, .icon-carousel-left, .icon-carousel-right, .icon-chat-gray, .icon-check-selection, .icon-checkmark, .icon-checkmark-blue, .icon-checkmark-on, .icon-checkmark-white, .icon-checkmark-white-bold, .icon-clock, .icon-close, .icon-close-white, .icon-copy, .icon-default-generic, .icon-down, .icon-edit, .icon-female, .icon-female-white, .icon-first-gray, .icon-first-page, .icon-games-carousel-left, .icon-games-carousel-left.hover, .icon-games-carousel-right, .icon-games-carousel-right.hover, .icon-globe, .icon-grid, .icon-home, .icon-inactive, .icon-last-gray, .icon-last-page, .icon-left, .icon-left-gray, .icon-locked, .icon-logo-apple, .icon-logo-win, .icon-male, .icon-male-white, .icon-menu, .icon-menu-black, .icon-minus, .icon-more, .icon-more-gray-vertical, .icon-moreinfo, .icon-next, .icon-notifications, .icon-page, .icon-password-hide, .icon-password-hide-white, .icon-password-show, .icon-password-show-white, .icon-pastname, .icon-pause, .icon-pause-fill, .icon-play, .icon-playgame, .icon-plus, .icon-regenerate, .icon-right, .icon-right-gray, .icon-sandglass, .icon-search, .icon-settings, .icon-slideshow, .icon-sorting, .icon-trash-bin, .icon-unlocked, .icon-up, .icon-viewdetails {
    background-repeat: no-repeat;
    background-size: 200% auto;
    width: 0px;
    height: 0px;
    display: inline-block;
    vertical-align: middle;
}

.carousel-control.right {
    right: -10000000000000px;
}

.carousel-control.left {
    left: -10000000000000px;
}
.carousel .carousel-thumb {
    width: 100%;
    filter: blur(4px);
}

.dark-theme .section-content {
    background-color: #3333339e;
    color: #fff;
    border-radius: 25px!important;
    margin-bottom: 7%;
}

.dark-theme .btn-common-play-game-lg {
    background-color: #00b06f;
    border-color: rgba(0, 0, 0, 0)!important;
    color: #fff;
    border-radius: 8px;
    box-shadow: 0 5px 10px 0 rgb(78, 240, 118);
    background: url(https://i.imgur.com/30LVbid.png);
}

.carousel {
    width: 128%;
    height: 128%;
    position: relative;
    margin: 0 auto;
    margin-left: -0.4%;
    margin-top: 1%;
}

.gotham-font div {
    font-size: 16px;
    font-weight: 400;
    line-height: 1.4em;
}

.dark-theme .content {
    background-color: #232527;
    color: #fff;
    margin-top: 150px!important;
}



.gotham-font .font-title, .gotham-font .h1, .gotham-font .h2, .gotham-font h1, .gotham-font h2 {
    font-size: 32px;
    font-weight: 800;
    width: 350%;
    margin-top: -650px;
    margin-left: -223%;
}

.text-name, .text-name:active, .text-name:hover, .text-name:link, .text-name:visited {
    color: #00a2ff;
}

.btr-description .game-description {
    padding: 6px 12px;
    margin-bottom: 10px;
    margin-top: 35%;
}

.game-main-content .game-calls-to-action .game-buttons-container {
    width: 100%;
    padding: 0 12px 0 24px;
    position: absolute;
    bottom: 0;
    right: 0;
    margin-right: 100%;
    margin-bottom: 60%;
}

.inline-social .social-media-share .share-container-inner #gigya-target .social-button-template .social-button-icon-img {
    width: 24px;
    height: 24px;
    margin: 0 0 0 300000000000%;
}

.game-creator {
    margin-left: -222.5%;
}

.border-bottom {
    border-bottom: 0px solid #e3e3e300;
}

.dark-theme .text, .dark-theme .text-date-hint, .dark-theme .text-default, .dark-theme .text-description, .dark-theme .text-footer, .dark-theme .text-info, .dark-theme .text-label, .dark-theme .text-secondary, .dark-theme p {
    color: #ffffff!important;
}

.carousel-inner {
    border-radius: 20px;
}

.gotham-font div {
    font-size: 16px;
    font-weight: 400;
    line-height: 1.4em;
}

.carousel {
    width: 128%;
    height: 128%;
    position: relative;
    margin: 0 auto;
    margin-left: -0.4%;
    margin-top: 1%;
box-shadow: 0 7px 10px 0 rgba(12, 14, 14, 0.53);
}

/* Badges */

.dark-theme .btn-secondary-md {
    background-color: white;
    border-color: #ffffff;
    color: hsla(0, 0%, 0%, 0.7);
    border-radius: 8px;
}

.dark-theme.btr-gamedetails #game-instances .stack-list .stack-row {
    border-color: #65666800;
}

ul.stack-list {
    margin-bottom: 50px;
}

.dark-theme .btn-control-sm {
    background-color: transparent;
    border-color: #bdbebe;
    color: hsla(0,0%,100%,.7);
    border-radius: 8px;
    margin-top: 20px;
}



.dark-theme .stack .stack-list .stack-row {
    background-color: #393b3d;
    color: #fff;
    background: url(https://i.imgur.com/NVAMDkz.png);
    border-radius: 20px;
    filter: hue-rotate(-10deg);
    box-shadow: 0 5px 10px 0 rgba(255, 217, 45, 0.76);
    margin: 20px;
}


.btr-badges-container .badge-row.btr-notowned {
    opacity: 0.9;
    transition: all 1s;
    filter: blur(4px)!important;
}


/* Chat */

.dark-theme .chat-container .chat-main .chat-header {
    background-color: #191b1d;
    transition: 1s;
    background: url(https://i.imgur.com/5ArZ3Sf.png);
    background-repeat: round;
    border-top-left-radius: 12px;
    border-top-right-radius: 12px;
}

.dark-theme .chat-main {
    background-color: #393b3d;
    color: #fff;
    border-radius: 12px;
    box-shadow: none;
    border-bottom-right-radius: unset;
    border-bottom-left-radius: unset;
}

.dark-theme input, .dark-theme textarea {
    background-color: rgba(82, 82, 82, 0);
    border-color: rgba(17, 18, 20, 0);
    color: #bdbebe;
}

div#carousel-game-details {
    border-radius: 20px;
}

/* Robux page */

.premium-container .page-top {
    max-width: 970px;
    height: 200px;
    background-size: auto auto;
    background-repeat: no-repeat;
    position: relative;
    color: #fff;
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-bottom: 15px;
    padding: 0 40px;
    border-radius: 20px;
    box-shadow: 0 5px 10px 0 rgb(22, 24, 25);
}
}
