@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("briefcase.yahoo.co.jp") {

/* Set Font */
*{font-family: Tahoma, Lucida Grande, sans-serif !important;
letter-spacing: 0.02em !important;}

/* Background */
body {background: #E5E5E5 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAACWCAIAAACQFHUlAAAAGXRFWHRTb2Z0d2FyZQBBZG9iZSBJbWFnZVJlYWR5ccllPAAAAEBJREFUeNpi2Lx5MxMDAwMKZmRkJCiGj09tNjny1BCDywFpRnT7sYjRwkxkMaL1EMtmALIZGYjm4xLDkh4AAgwA9hwD2l0/KckAAAAASUVORK5CYII=") top left repeat-x !important;
margin-top: 10px !important;}

html > body > center > p {margin-top: -53px !important;}

/* Text Link, Visited Color */
a:link {color: #333 !important;} 
a:visited {color: #555 !important;}

/* Table Color */
tr[bgcolor="#cccc99"] {background: none !important;}
td[bgcolor="#e6e6c8"] {background: none !important;}
td[bgcolor="#e0ddbc"] {background: #BBB !important;}
td[bgcolor="#faedac"] {background: #AAA !important;}
table[bgcolor="#999999"] {background: none !important;}
table[bgcolor="#ffffff"] {background: none !important;}
tr[bgcolor="#dcdcdc"] {background: #CCC !important;}
tr[bgcolor="#eeeeee"] {background: #F6F6F6 !important;}
html > body > center > p > table > tbody > tr > td > form > table > tbody > tr > td > table > tbody > tr[bgcolor="#ffffcc"] {background: none !important;}
/* Edit Page Table Color */
tr[bgcolor="#ffffcc"] {background: none !important;}
tr[bgcolor="#e6e6c8"] {background: #E0E0E0 !important;}
tr[bgcolor="#f9dfa4"] {background: #CCC !important;}
font[color="#ff9933"] {color: #777 !important;}

/* Upload and Add Folder */
html > body > center > p > table > tbody > tr > td > form > table > tbody > tr > td > font > a[href*="http://edit.briefcase.yahoo.co.jp/edit/"]{
background: #555 !important;
color: #EEE !important;
padding: 3px 9px 3px 9px !important;
-moz-border-radius: 12px !important;}

/* Under Button Size */
input[type="submit"] {width: 120px !important; height: 30px !important;}

/* Y!Top Logo */
img[src="http://i.yimg.jp/i/jp/bc/bc_main.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 310px !important;}

/* Folder Icon */
img[src="http://i.yimg.jp/i/jp/bc/sfld.gif"],
img[src="http://i.yimg.jp/i/jp/bc/folder.gif"] {
background: url(data:image/png;base64,) !important;
width: 0 !important;
padding-left: 16px !important;
margin-left:-1px !important;}

/* Send Mail Button */
input[src="http://i.yimg.jp/i/jp/bc/bot_pt2.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 115px !important;
margin-bottom: -4px !important;}

/* Move Button */
input[src="http://i.yimg.jp/i/jp/bc/sbot_mov.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;}

/* Copy Button */
input[src="http://i.yimg.jp/i/jp/bc/sbot_cop.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;}

/* Delete Button */
input[src="http://i.yimg.jp/i/jp/bc/sbot_del.gif"] {
background: url(data:image/png;base64,) !important;
background-repeat: no-repeat !important;
width: 0 !important;
padding-left: 35px !important;}

/* Remove Element */
#uhdMain,
html > body > center > center,
html > body > center > p > table > tbody > tr[bgcolor="#dcdcdc"],
html > body > center > p > table > tbody > tr > td[width="170"],
html > body > center > form > table > tbody > tr > td[width="170"],
html > body > center > table > tbody > tr[bgcolor="#dcdcdc"],
html > body > center > p > table > tbody > tr > td > font > b,
html > body > center > table > tbody > tr > td > font[size="-1"],
td[width="10"], table[width="468"] {
display: none !important;}
}
