color: var(--text1);
background-color: var(--back2);
border-radius: var(--borderradius);
padding: 7px;
}

[data-region="confirm-dialogue"] {
    background-color: var(--back2) !important;
}

.yui3-panel.moodle-dialogue {
/* General dialogue settings*/
outline: none;
border-radius: var(--borderradius);
box-shadow: var(--shadow);
}

