.moodle-dialogue-base .modal-header .closebutton,
button.close,
.moodle-dialogue-base button.closebutton {
color: var(--white);
opacity: 1;
font-size: 1rem;
font-weight: 100;
width: 12px;
height: 12px;
line-height: 0;
border-radius: 20px;
text-shadow: none;
background-color: var(--red);
background-image: none;
box-shadow: var(--shadowcard);
padding: 7px;
display: flex;
justify-content: center;
}

.comment-area .db textarea {
color: var(--text1) !important;
}
.comment-ctrl ul.comment-list li[style="color: rgb(0, 102, 238); background-color: rgb(255, 227, 144);"] {
background-color: var(--yellowB2) !important;
color: var(--text1) !important;
}

/* Add resource pane */
#chooseform .instruction,
.chooserdialogue .moodle-dialogue-wrap .moodle-dialogue-bd{
    background: transparent !important;
}

.jschooser .choosercontainer #chooseform .instruction, .jschooser .choosercontainer #chooseform .typesummary {
    background: var(--back3);
}

.choosercontainer #chooseform .selected, /*Resource pane selection with style below*/

/* Little calendar widget (on stats for teachers) */
.nav-tabs .nav-link.active,
.nav-tabs .nav-item.show .nav-link {
    color: var(--text1);
    background-color: var(--accent);
}

.nav-tabs .nav-link {
    border-top-left-radius: var(--uiborderradius);
    border-top-right-radius: var(--uiborderradius);
}




/* ################# GRADING PLATFORM ################# */
.path-mod-assign [data-region="grade-panel"] {
    background-color: var(--back2);
}

.path-mod-assign [data-region="grade-panel"] .fitem.has-popout {
    background-color: var(--back1);
}

.path-mod-assign [data-region="grade-actions-panel"] {
    border-top: none;
}

.mform fieldset,
.path-mod-assign [data-region="assignment-info"],
.path-mod-assign [data-region="grading-navigation-panel"]{
    border-bottom: none;
}

/* Time usage clock */
[data-block="teacher_timer"] > .card-body > .card-text > span {
    color: var(--text3) !important;
}
[data-block="teacher_timer"] > .card-body > .card-text > [alert="card-body"] {
    background-color: var(--accent) !important;
    color: var(--text1) !important;
}
[data-block="teacher_timer"] > .card-body > .card-text > div:not(.card-body):not([alert="card-body"]) {
    background: var(--blueB1) !important;
    color: var(--blueF1) !important;
}
}

@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/") {
/* MAIN HOME */
#page-header {
/* Moodle home img background (force method)*/
background-color: transparent !important;
}
.coursebox > .info > .coursename_semi a,
.coursebox > .info > .coursename_pre a {
color: gray;
filter: brightness(2);
}

#page-header.portada .container-fluid {
color: white;
padding-top: 30px;
padding-bottom: 15px;
background: rgba(12, 17, 90, .7) !important;
border: 1px solid rgba(255, 255, 255, 0.1);
backdrop-filter: blur(7px);
box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);
text-shadow: 0px 0px 7px rgba(0, 0, 0, 0.5);
}

#page-header.portada {
    margin-bottom: 1rem;
}
}

@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/") {
/* #################### CHAT #################### */
/* ########## Left pane & general ########## */
.message-app {
    background-color: var(--back2);
}

.message-app a {
    color: var(--text2);
}

.message-app .day {
    color: rgba(var(--fAlpha), 0.3);
    font-size: 80%;
}

.message-app .border-top {
    border-top: 1px solid var(--borderL) !important;
}

/* Usable drawer width */
[data-region=right-hand-drawer].drawer {
    top: 88px;
    bottom: 0px;
    height: auto;
    width: 400px;
}

.message-app .border-bottom {
    border-bottom: none!important;
}

.message-app .card {
    margin-bottom: 0.5em;
}


/* Destacados / Grupo / Privado */
.message-app .btn-link:not(.btn-icon) {
    background-color: var(--back1);
}
.message-app .btn-link:hover {
    background-color: rgba(var(--bAlpha), 0.4);
}
.message-app .btn-link:focus {
    background-color: rgba(var(--bAlpha), 0.5);
}

[data-region="view-overview-messages"] > div > .list-group > .list-group-item > div.w-100.text-truncate {
    margin-bottom: 1rem !important;
}

/* <Verlo todo> */
.message-app .footer-container > .text-center > a {
    display: block;
    background-color: var(--back3);
    color: var(--text2);
    padding: 5px;
    border-radius: var(--uiborderradius);
    margin-top: 5px;
    margin-bottom: 15px;
}

.message-app .footer-container > .text-center > a:hover {
    background-color: var(--back5);
    color: var(--text1);
    text-decoration: none;
    padding: 5px;
    border-radius: var(--uiborderradius);
    margin-top: 5px;
    margin-bottom: 15px;
}

@keyframes bg-pulse-grey {
  0% {
    background-color: rgba(var(--bAlpha),0.05);
  }
  50% {
    background-color: rgba(var(--bAlpha),0.1);
  }
  100% {
    background-color: rgba(var(--bAlpha),0.05);
  }
}

.emoji-picker .category-button {
    margin: 1px;
}
.emoji-picker .category-button.selected {
    border-bottom-width: 3px;
}

/* -- Chat bubbles -- */
.message-app .message.send {
    background-color: var(--accentB2);
    color: var(--text1);
    margin-left: auto!important;
}
.message-app .message.send .tail {
  border-bottom-color: var(--accentB2);
}

.message-app .message.received {
  background-color: var(--back5);
  color: var(--text1);
    margin-right: auto!important;
}
.message-app .message.received .tail {
  border-bottom-color: var(--back5);
}

.message-app .message.send .time,
.message-app .message.received .time{
  color: rgba(var(--fAlpha), 0.5);
}

.message-app .message{
    max-width: 80%;
}

.message-app .clickable:hover {
    box-shadow: 5px 5px 20px rgba(0,0,0,0.4);
}
}

@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/message/") {
