@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/login/index.php") {
/* #################### LOGIN PAGE #################### */
:root {
--blur: 7px
}

body {
background-color: transparent;
}

body #bg-login {
/*filter: blur(5px) brightness(0.35);*/
background-image: none;
background-color: var(--back1)
}

a:link,
a:hover,
a:visited,
a:active,
span.flagged-tag a {
color: white;
}

.navbar {
background: var(--unapecBlue1);
}

/* Mother force to make the logo darkmode proof :) */
header .logo img {
width: 146px;
height: 44px;
object-fit: cover;
object-position: 100% 0;
filter: grayscale(1) brightness(5);
}
a.logo:before {
content: "";
background: transparent url("https://eva.unapec.edu.do/moodle/theme/boost/pix/logo_azul.png") no-repeat;
width: 34px;
height: 44px;
display: inline-block;
vertical-align: middle;
margin-right: 5px;
}

/* Block pieces */
