}
.botones .ayuda {
background-color: rgba(var(--bAlpha), 0.35);
}
.botones .preg-frec {
background-color: rgba(var(--bAlpha), 0.4);
}
.botones .wifi {
background-color: rgba(var(--bAlpha), 0.45);
}
.botones .aulatec {
background-color: rgba(var(--bAlpha), 0.5);
}
.botones .aulatec a {
filter: brightness(3) saturate(2) hue-rotate(40deg);
}




.info-eva .texto {
background: rgba(0, 44, 73, 0.6);
backdrop-filter: blur(var(--blur));
padding-top: 30px
}

.ejmpl {
background: var(--back4);
}
.ejmpl strong {
color: #0087ff;
}

h2,
.wpr-login ul.links a,
#btn-nivel{
color: var(--text3)
}

.nota-info p,
#btn-nivel {
background-color: var(--back4);
}

.wpr-login {
background: var(--back2);
}
.curva-vert {
display: none;
}
}

@-moz-document url-prefix("https://eva.unapec.edu.do/moodle/calendar/") {
/* #################### CALENDAR PAGE #################### */
