style_setting_options.value:
/* ~~~ Light theme ~~~ */
@media (prefers-color-scheme: light) {
    :root {
        --text1: #181b20;
        --text2: #1f232a;
        --text3: #262b33;
        --text4: #2b323b;
        --text5: #333a45;

        --back5: #ffffff;
        --back4: #f8f8f8;
        --back3: #efefef;
        --back2: #e7e7e7;
        --back1: #d5d5d5;

        --accent: var(--blue);
        --accentF2: var(--blueF1);
        --accentF1: var(--blueF2);
        --accentB2: var(--blueB2);
        --accentB1: var(--blueB1);

        --link: var(--blueF2);
        /*#758bff;*/
        --border: rgba(255, 255, 255, 0.3);
        --borderL: rgba(255, 255, 255, 0.15);

        /* UNAPEC */
        --unapecBlue1: #022646;
        --unapecBlue2: #02335E;
        --unapecBlue3: #023f75;
        --unapecBlue4: #0058a6;
        --navBarBlue: var(--unapecBlue1);

        /* Transparencies */
        --fAlpha: 0, 0, 0;
        --bAlpha: 255, 255, 255;

        /* Colors */
        --red: #f44336;
        /*500*/
        --redB1: #ffcdd2;
        /*100*/
        --redB2: #ef9a9a;
        /*200*/
        --redF1: #b71c1c;
        /*900*/
        --redF2: #d32f2f;
        /*700*/
        --orange: #ff9800;
        --orangeB1: #ffe0b2;
        --orangeB2: #ffcc80;
        --orangeF1: #e65100;
        --orangeF2: #f57c00;

        --yellow: #ffeb3b;
        --yellowB1: #fff9c4;
        --yellowB2: #fff59d;
        --yellowF1: #f57f17;
        --yellowF2: #fbc02d;

        --green: #4caf50;
        --greenB1: #c8e6c9;
        --greenB2: #a5d6a7;
        --greenF1: #1b5e20;
        --greenF2: #388e3c;

        --blue: #2195f3;
        --blueB1: #bbdefb;
        --blueB2: #90caf9;
        --blueF1: #0d47a1;
        --blueF2: #1976d2;

        --indigo: #3f51b5;
        --indigoB1: #c5cae9;
        --indigoB2: #9fa8da;
        --indigoF1: #1a237e;
        --indigoF2: #283593;

        --purple: #9c27b0;
        --purpleB1: #e1bee7;
        --purpleB2: #ce93d8;
        --purpleF1: #4a148c;
        --purpleF2: #7b1fa2;

        /* Non color variables*/
        --borderradius: 10px;
        --uiborderradius: 5px;
        --shadow: 0px 0px 12px rgba(0, 0, 0, 0.4);
        --shadowcard: 0px 0px 8px rgba(0, 0, 0, 0.1);
    }

    /* Main page */
    .coursebox > .info > .coursename_semi a,
    .coursebox > .info > .coursename_pre a {
        color: gray;
        filter: brightness(0.75);
    }

    /* Navbar & footer */
    .navbar.navbar-expand {
        background: var(--back2) !important;
        box-shadow: var(--shadow);
    }
    footer{
        background: rgba(var(--fAlpha),0.25) !important;
        background: linear-gradient(0deg, rgba(var(--fAlpha),0.5) 10%, rgba(var(--fAlpha),0.0)) !important;
    }
    footer .direccion {
        color: rgba(255,255,255, 0.7);
    }
    footer a,
    footer a:link,
    footer a:visited {
        color: rgba(40,40,200,0.8) !important;
    }

    /* Replace header logo with footer logo :) */
    .d-md-inline img {
        background: url("https://eva.unapec.edu.do/moodle/theme/boost/pix/logo_azul.png") no-repeat;
        display: block;
        -moz-box-sizing: border-box;
        box-sizing: border-box;
        width: 180px;
        height: 44px;
        padding-left: 180px;
        background-size: contain;
    }

    /* Download arrow of file manager */
    .dndupload-arrow {
        /* Upload file image*/
        background-image: url("data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg' fill-rule='evenodd' clip-rule='evenodd'%3E%3Cpath fill='%23333333' d='M11 21.883l-6.235-7.527-.765.644 7.521 9 7.479-9-.764-.645-6.236 7.529v-21.884h-1v21.883z'/%3E%3C/svg%3E");
        background-size: 60px;
    }
}

