css:
@-moz-document url-prefix("https://zspdabrowka.pl/spdabrowka/") {
.widget_recent_entries{
    background-color: rgba(39, 39, 39, 0.911) !important;
    border-radius: 0% !important;
    border: solid white 2px !important;

}
.widget_text{
    background-color: rgba(39, 39, 39, 0.911) !important;
    border-radius: 0% !important;
    border: solid white 2px !important;

}
a{
    text-decoration: none !important;
    font-family: 'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif !important;
    color: rgb(134, 176, 211) !important;
}
body{
    font-family: 'Franklin Gothic Medium', 'Arial Narrow', Arial, sans-serif;
    background-color: rgb(34, 34, 34);
}
.main-navigation ul li ul {
    border-color: rgb(134, 176, 211) !important;
    background-color: #101010 !important; 
}
.main-navigation ul li a, .main-navigation ul li a:visited{
    border-radius: 0%;
    background-color: #101010;
}
#search-wrapper input[type="search"] {
    background-color: #101010;
    border-color: rgb(134, 176, 211);
    color: gainsboro;
    text-shadow: none;
    border-radius: 0%;
}
#sfsi_floater {
    margin-top: 0px;
    margin-left: 0px;
    height: 0%;
    width: 0%;
}
#main-content-wrapper {
    background-color: rgb(27, 27, 27);
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
}
#search-wrapper input[type="search"] {
    background-color: #101010 !important;
    border-color: rgb(134, 176, 211) !important;
    color: rgb(134, 176, 211) !important;
    text-shadow: none !important;
}

}
