css:
@-moz-document domain("dom.mck-ktits.ru") {
body {
	background: #1c1c1c;
}

a {
    color: #2a52be;
}

.card, .que .history, .userprofile .profile_tree section, .groupinfobox, .well, .navbar, .region_main_settings_menu_proxy, #region-main-box {
	background: #1d1d1d !important;
}


#region-main {
	background: #1d1d1d;	
}

#region-main, #inst32 {
	margin-top: 20px;
}

.content .summary {
	display: none;
}

#nav-drawer {
	background: #1c1c1c;
    overflow: hidden;
}

#nav-drawer:hover {
	overflow: auto;
}

#nav-drawer .list-group li:hover a {
	background: #1a1a1a !important;
}

.list-group-item:last-child {
	background: #1e1e1e;
    border: 1px solid #1b1b1b;
}

.list-group-item.active {
 	background: #1d1d1d;
    color: darkgray;
}

.bg-gray {
	background-color: #1f1f1f !important;
    color: white;
    border: 0;
}

button.btn.nav-link.float-sm-left.mr-1.btn-light.bg-gray:hover {
    background: #1a1a1a !important;
    color: white;
}

.page-header-headings {
	color: white;
}

.attostylesbox.attostylesbox-outline.attostylesbox-outline-blue {
	border: 0;
    border-left: 3px solid #7d9fd3;
    background: #1a1a1a;
}

.moodle-dialogue-base .moodle-dialogue.mod_quiz_preflight_popup .moodle-dialogue-wrap {
	background: #1f1f1f;
}

.pagination li a {
	background: #1c1c1c;
    border: 1px solid #1a1a1a;
}

.course-content ul li.section.main {
	border-bottom: 1px solid #1a1a1a;
}

#page-wrapper #page {
	background: #1c1c1c;
}

.page-item.disabled a {
	background: #1c1c1c !important;
    border: 1px solid #1a1a1a !important;
}

::-webkit-scrollbar-thumb {
    background-image: linear-gradient(to bottom, #00AFEA, #00a859, #FFC829, #ED3437);
    border-radius: 4px;
}

::-webkit-scrollbar-track {
    background: #1f1f1f;
}

::-webkit-scrollbar {
    width: 8px;
}
}
