css:
@-moz-document domain('npa2009.org') {
    body {
        background: #202020;
        color: #d0d0d0;
    }
    #zone-branding-wrapper {
        background: transparent !important;
    }
    .bloc-vert .views-row a {
        color: white !important;
    }
    font[color="#000000"] {
        color: #d0d0d0;
    }
    .not-front {
        background: #202020;
    }
    .menu-princip a:link, .menu-princip a:visited, .menu-princip a:active, .menu-princip a:hover, .bloc-noir .views-row a {
        color: white;
    }
    tr:hover td, tr.even:hover td.active, tr.odd:hover td.active {
        background: transparent;
    }
    .pied_logos tr td p {
        background: white;
    }
    .bloc-vert-f .views-row a {
        color: #539a62;
    }
    .bloc-violet .views-row a {
        color: #bc52ff;
    }
    .sf-menu.sf-horizontal.sf-shadow ul, .sf-menu.sf-vertical.sf-shadow ul, .sf-menu.sf-navbar.sf-shadow ul ul, #superfish-2 li ul li {
        background: black;
    }
    textarea, select, input[type="date"], input[type="datetime"], input[type="datetime-local"], input[type="email"], input[type="month"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="time"], input[type="url"], input[type="week"] {
        background: #111;
        border-color: #222;
        color: white;
    }
    body a:link, body a:visited, .item-list-themes h2, .item-list-themes .views-field-title a, item-list-themes, .page-taxonomy-term article .node-title a {
        color: #e0001b;
    }
    .logo-img img {
        background: white;
    }
}
