css:
@-moz-document domain(voz.vn) {
.block-filterBar {
background: #3a3a3a;
}

.structItem-minor {
    font-size: 0.75rem;
    color: #c9c9c9;
}

.structItem {
    display: table;
    table-layout: fixed;
    border-collapse: collapse;
    list-style: none;
    background: #262626;
    border-top: 2px solid #3a3a3a;
}
.input {

    font-size: 1rem;
    color: #c9c9c9;
    background: #3a3a3a;
    border-width: 1px;
    border-style: solid;
    border-top-color: #3a3a3a;
    border-right-color: #3a3a3a;
    border-bottom-color: #3a3a3a;
    border-left-color: #3a3a3a;
    border-radius: 4px;
    padding: 8px;
    display: block;
    width: 100%;
    line-height: 1.5;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;

}
.input:focus, .input.is-focused {
    outline: 0;
    background: #3a3a3a;

}
.fr-box.fr-basic .fr-element {

    color: #c9c9c9;

}


a {
    color: #c9c9c9;
    text-decoration: none;
    font-size: 1rem;
}
.p-sectionLinks {
    font-size: 0.875rem;
    color: #c9c9c9;
    background: #3a3a3a;
    border-bottom: none;
}
.message + .message, .message.message--bordered {
    border-top: none;
}
.message-attribution {
    color: #c9c9c9;
    font-size: 1rem;
    padding-bottom: 3px;
    border-bottom: 1px solid #3a3a3a;
}
.bbCodeBlock-expandLink {
    display: block;
    background: transparent;
}
.reactionsBar {
    transition-property: all, -xf-opacity;
    transition-property: all,-xf-height;
    background: #212121;
    border: 1px solid #212121;
        border-left-color: rgb(203, 205, 208);
        border-left-style: solid;
        border-left-width: 1px;
    border-left: 2px solid #5c7099;
    padding: 8px;
    font-size: 0.75rem;
    margin-top: 8px;
}
.p-title-value {
    color: #c9c9c9;
}
.bbCodeBlock-expandContent {
    color: #c9c9c9;
    font-style: italic;
    background: #262626;
}
.bbCodeBlock {
    border: 1px solid #3a3a3a;
    color: #c9c9c9;
    font-style: italic;
    background: #262626;
}
.bbCodeBlock-title {
    color: #c9c9c9;
    background: #262626;
}
aside.message-signature {
    color: #484141;
}
.message-signature {
    margin-top: 12px;
    font-size: 0.875rem;
    color: #8f9193;
    border-top: 1px solid #3a3a3a;
    padding-top: 10px;
    border-top-left-radius: 50px;
}

.p-header {

    background: #262626;

}
.p-nav {

    background: #262626;

}
.pageNav-page {
    border-right: 1px solid #B0BDC6!important;
    background: #e1d9d9;
}
.message-cell.message-cell--main {
    background: #262626;
}
.message-cell.message-cell--user, .message-cell.message-cell--action {

    position: relative;
    background: #262626;
    border-right: 1px solid #262626;
    min-width: 0;

}
.block--messages .message, .block--messages .block-row {

    color: #c9c9c9 !important;
}
.p-body {
    display: flex;
    align-items: stretch;
    flex-grow: 1;
    min-height: 1px;
    background: #212121;
    background-color: #000;

background-image: url(data:image/png;base64,) !important;
}
.fr-toolbar {
    color: #15191d;
    background: none;
    position: relative;
    z-index: 4;
    font-family: 'Be Vietnam','Segoe UI','Helvetica Neue',Helvetica,Roboto,Oxygen,Ubuntu,Cantarell,'Fira Sans','Droid Sans',sans-serif;
    border-top: 4px solid #3a3a3a;
    border: 2px solid #3a3a3a;
}
.fr-separator {
    background: #3a3a3a;
    display: block;
    float: left;
}
.fr-box.fr-basic .fr-wrapper {
    background: none;
    border: 2px solid #3a3a3a;
    top: 0;
    left: 0;
}
.block-container {
    color: #c9c9c9;
    background: #262626;
    border-top-color: #3a3a3a;
    border-right-color: #3a3a3a;
    border-bottom-color: #3a3a3a;
    border-left-color: #3a3a3a;
    border-radius: 4px;
}
.block-header {
    padding: 8px 12px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 1.25rem;
    color: #c9c9c9;
    background: #3a3a3a;
    border-bottom: 1px solid #262626;
}
.node + .node {
    border-top: 1px solid #343434;
}
.inputGroup.inputGroup--joined .inputGroup-text {
    border-width: 1px;
    border-style: solid;
    border-top-color: #3a3a3a;
    border-bottom-color: #3a3a3a;
    border-left-color: #3a3a3a;
    background: #3a3a3a;
    text-align: center;
    padding: 4px 8px;
}
}
