css:
@-moz-document domain("moddb.com") {
#body {
    width: auto;
    min-width: 870px;
    max-width: 2000px;
}
@media(max-width: 1000px) {
    #header a.logo {
        width: 200px;
        height: 77px;
        margin-top: 43px;
        background-repeat: no-repeat;
        background-size: cover;
    }
}
#subheader ul {
    margin: 0 16px;
}
#subheader ul li {
    width: auto;
}
#subheader ul li a {
    padding: 0 10px;
}
.span-all {
    width: 100%;
}
@media(min-width: 1025px) {
        div.normalcorner {
            background-image: none;
            background-color: #000;
        }
    }
.imagebox {
    display: flex;
    justify-content: center;
}
div.imagebox ul {
    display: flex;
    width: 100%;
}
div.imagebox li {
    float: none;
    width: 100%;
    display: flex;
    align-items: center;
}
div.imagebox li.previous, div.imagebox li.next {
    width: auto;
}
div.imagebox a, div.imagebox span.empty {
    width: 100%;
    height: 100%;
    position: relative;
}
div.imagebox li img {
    width: 100%;
    height: 100%;
    max-width: 100%;
    max-height: 100%;
}
div.imagebox span.play {
    width: 100%;
    height: 100%;
}
.span-300-alt {
    width: calc(100% - 325px);
}
.row {
    display: flex;
}
#articlesbrowse .rowcontent {
    display: block;
}
div.normalbox div.table a.image {
    float: none;
}
div.normalbox div.table div.content {
    width: 100% !important;
}
div.rotatorholder {
    width: 100%;
    height: 0;
    padding-bottom: calc(40% + 10px);
}
div.rotatorbox {
    width: 100%;
    height: 100%;
}
div.rotatorbox div.rotatorcorner {
    height: 0;
    padding-bottom: 40%;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center center;
    image-rendering: crisp-edges;
}
div.rotatorbox div.title {
    margin-top: 0;
    position: absolute;
    bottom: 5px;
    left: 5px;
    right: 5px;
}
@media(min-width: 1400px) {
    div.rotatorbox div.rotatorcorner {
        padding-bottom: 30%;
    }
    div.rotatorholder {
        padding-bottom: calc(30% + 10px);
    }
}
@media(min-width: 1600px) {
    div.rotatorbox div.rotatorcorner {
        padding-bottom: 20%;
    }
    div.rotatorholder {
        padding-bottom: calc(20% + 10px);
    }
}
#footer {
    padding: 0 10px;
}
#footer div.content {
    width: 100%;
}
}
