css:
@-moz-document url-prefix("https://zoom.us") {
html, body {
    background-color: #8c369e;
}

.join-frame #content_container {
    background-color: #8c369e;
    min-height: 520px;
    padding-top: 94px;
    padding-bottom: 30px;
}

.join-frame #footer_container {
    background-color: #8c369e;
    border-top: 0;
    height: auto;
}

.launch-section {
    background-color: #7380fb;
    box-shadow: 0 1px 1px rgb(220, 75, 255);
    min-height: 480px;
    position: relative;
}

#header_container {
    position: fixed;
    width: 100%;
    top: 0;
    z-index: 300;
    background-color: rgb(210, 0, 255);
    height: 64px;
    line-height: 64px;
    box-shadow: 0 0 2px rgba(0,0,0,0.2);
}

.launch-section.chrome .desc13 {
    color: #0014ff;
    padding: 140px 0 100px;
}

.launch-section .desc12, #embed_container.firefox30 .desc13 {
    color: #ff4fac;
    font-size: 22px;
    font-weight: 300;
    padding: 40px 0 40px;
}

.launch-section .desc21, .launch-section .desc22, .launch-section.firefox30 .desc23 {
    color: #f72959;
    font-size: 18px;
    font-weight: 400;
    position: absolute;
    bottom: 30px;
    max-width: 840px;
    width: 100%;
}

.launch-section .desc24 {
    padding-bottom: 30px;
    color: #fb3554;
}

.join-frame #footer_container p, .join-frame #footer_container p a {
    color: #2700ff;
    font-size: 11px;
    font-weight: 300;
}

#content_container {
    padding-top: 64px;
    position: relative;
    background-color: #8c369e;
    min-height: 624px;
}

#footer_container {
    width: 100%;
    min-height: 88px;
    background-color: #8c369e;
}

.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #8c369e;
    background-image: none;
    border: 1px solid #ccc;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
}

.btn-primary {
    background-color: #d200ff;
    border-color: #d200ff;
    color: #9482f9;
}

.btn.btn-primary:hover, .btn.btn-primary:focus {
    background-color: #d15bea;
    border-color: #d200ff;
}

.form-control:focus {
    border-color: #d200ff;
    box-shadow: none;
}
}
